import React from 'react';
import {
  Box,
  Typography,
  Button,
  Paper,
  Collapse,
  CircularProgress,
} from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import { alpha } from '@mui/material/styles';
import { useBooks } from '../../context/BookContext';

const TaskCard = ({
  icon: Icon,
  title,
  description,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonText,
  children,
  customActions,
  generateButtonStyle
}) => {
  const { isAnyTaskGenerating, setIsAnyTaskGenerating } = useBooks();

  const handleGenerate = async () => {
    setIsAnyTaskGenerating(true);
    await onGenerate();
    setIsAnyTaskGenerating(false);
  };

  return (
    <Paper sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
        <Box sx={{ mr: 3, backgroundColor: '#F8F9FF', borderRadius: '12px', p: 2 }}>
          <Icon sx={{ fontSize: 48, color: 'primary.main' }} />
        </Box>
        <Box sx={{ flexGrow: 1 }}>
          <Typography variant="h6" gutterBottom component="div">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }} component="div">
            {description}
          </Typography>
          <Button
            variant="outlined"
            color="primary"
            onClick={onExpand}
            endIcon={isExpanded ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            disabled={isAnyTaskGenerating}
          >
            ESPANDI
          </Button>

          <Collapse in={isExpanded} timeout="auto" unmountOnExit>
            <Box sx={{ mt: 3 }}>
              <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={isGenerating ? <CircularProgress size={20} color="inherit" /> : <Icon />}
                  onClick={handleGenerate}
                  disabled={isGenerating || isAnyTaskGenerating}
                  sx={generateButtonStyle}
                >
                  {isGenerating ? 'GENERAZIONE IN CORSO...' : generateButtonText}
                </Button>
              </Box>

              <Collapse in={showContent && !isGenerating} timeout="auto" unmountOnExit>
                <Box sx={{ mt: 3 }}>
                  <Paper sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                    <Typography 
                      variant="body1" 
                      component="div"
                      sx={{ 
                        textAlign: 'justify',
                        whiteSpace: 'pre-wrap',
                        '& p': { marginBottom: '1em' }
                      }}
                    >
                      {children}
                    </Typography>
                  </Paper>

                  {customActions && (
                    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3, gap: 2 }}>
                      {customActions}
                    </Box>
                  )}
                </Box>
              </Collapse>
            </Box>
          </Collapse>
        </Box>
      </Box>
    </Paper>
  );
};

export default TaskCard;
