import React from 'react';
import TaskCard from './TaskCard';
import StoreIcon from '@mui/icons-material/Store';

const StoreDescriptionTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={StoreIcon}
      title="Genera la descrizione per gli store"
      description="Analizzerò il tuo libro e creerò una descrizione ottimizzata per gli store online."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA DESCRIZIONE"
      generateButtonStyle={generateButtonStyle}
    >
      {currentBook.metadata?.storeDescription?.storeDescription || ''}
    </TaskCard>
  );
};

export default StoreDescriptionTaskCard;
