import React from 'react';
import TaskCard from './TaskCard';
import { Box, Typography, Paper, IconButton, CircularProgress } from '@mui/material';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import RefreshIcon from '@mui/icons-material/Refresh';
import { alpha } from '@mui/material/styles';

const KeywordsTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  isRegeneratingKeyword,
  onRegenerateKeyword,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={LocalOfferIcon}
      title="Genera le parole chiave"
      description="Analizzerò il tuo libro e suggerirò quattro parole chiave pertinenti per migliorare la visibilità sugli store."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA KEYWORDS"
      generateButtonStyle={generateButtonStyle}
      allowRegenerate={false}
    >
      {currentBook.metadata?.keywords?.keywords?.map((keyword, index) => (
        <Paper key={index} sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="body1" sx={{ flex: 1 }}>
              {keyword}
            </Typography>
            <IconButton 
              size="small"
              onClick={() => onRegenerateKeyword(index)}
              disabled={isRegeneratingKeyword}
            >
              {isRegeneratingKeyword ? 
                <CircularProgress size={20} /> : 
                <RefreshIcon />
              }
            </IconButton>
          </Box>
        </Paper>
      ))}
    </TaskCard>
  );
};

export default KeywordsTaskCard;
