import React, { useState } from 'react';
import {
  Box,
  Typography,
  Button,
  Paper,
  CircularProgress,
  Alert,
  Snackbar
} from '@mui/material';
import { useBooks } from '../../context/BookContext';
import { useParams, useNavigate } from 'react-router-dom';

const CategorySection = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const { books, generateCategories, updateBookMetadata } = useBooks();
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const currentBook = books.find(book => book.id === id);

  React.useEffect(() => {
    if (!currentBook) {
      navigate('/');
    }
  }, [currentBook, navigate]);

  const handleGenerate = async () => {
    try {
      setError(null);
      setIsGenerating(true);

      const result = await generateCategories(currentBook.id);
      await updateBookMetadata(currentBook.id, {
        categories: result
      });

      setSuccess(true);
    } catch (error) {
      setError('Errore nella generazione delle categorie. Riprova.');
    } finally {
      setIsGenerating(false);
    }
  };

  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(false);
  };

  if (!currentBook) return null;

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h5" sx={{ mb: 3 }}>
        Categorie BISAC
      </Typography>

      <Paper sx={{ p: 3, mb: 3, backgroundColor: 'white' }}>
        {currentBook?.metadata?.categories ? (
          <Box>
            {Object.entries(currentBook.metadata.categories).map(([type, categories]) => (
              <Box key={type} sx={{ mb: 2 }}>
                <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 500 }}>
                  {type}
                </Typography>
                {categories.map((category, index) => (
                  <Typography key={index} variant="body2">
                    {category}
                  </Typography>
                ))}
              </Box>
            ))}
          </Box>
        ) : (
          <Typography color="text.secondary">
            Nessuna categoria generata. Clicca il pulsante per generarne.
          </Typography>
        )}
      </Paper>

      <Button
        variant="contained"
        onClick={handleGenerate}
        disabled={isGenerating}
        startIcon={isGenerating ? <CircularProgress size={20} color="inherit" /> : null}
        sx={{
          minWidth: '200px',
          height: '48px'
        }}
      >
        {isGenerating ? 'Generazione...' : 'Genera Categorie'}
      </Button>

      <Snackbar open={!!error} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      <Snackbar open={success} autoHideDuration={3000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
          Categorie generate con successo!
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CategorySection;
