import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Container,
  Paper,
  IconButton,
  Input
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';

const MainDashboard = () => {
  const navigate = useNavigate();
  const fileInputRef = React.useRef();

  const handleUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // In un'implementazione reale, qui faremo l'upload del file
      // Per ora simuliamo solo il successo e navighiamo alla TaskDashboard
      navigate('/tasks', { state: { bookName: file.name } });
    }
  };

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4, textAlign: 'center' }}>
        <Typography variant="h2" component="h1" gutterBottom>
          Benvenuto nel tuo Publishing Dashboard
        </Typography>
        <Typography variant="h5" color="text.secondary" gutterBottom>
          Carica il tuo libro per iniziare a ottimizzare la tua presenza online
        </Typography>

        <Paper
          sx={{
            mt: 4,
            p: 6,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            backgroundColor: 'background.paper',
          }}
        >
          <Box
            sx={{
              mb: 3,
              width: '48px',
              height: '48px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <IconButton
              sx={{
                color: 'primary.main',
                '&:hover': { backgroundColor: 'transparent' },
              }}
              disableRipple
            >
              <CloudUploadIcon sx={{ fontSize: 48 }} />
            </IconButton>
          </Box>

          <input
            type="file"
            ref={fileInputRef}
            style={{ display: 'none' }}
            onChange={handleUpload}
            accept=".pdf,.doc,.docx,.txt"
          />

          <Button
            variant="contained"
            color="primary"
            size="large"
            startIcon={<CloudUploadIcon />}
            onClick={handleButtonClick}
          >
            CARICA IL TUO LIBRO
          </Button>
        </Paper>
      </Box>
    </Container>
  );
};

export default MainDashboard;
