import React from 'react';
import {
  Box,
  Typography,
  Button,
  Container,
  Paper,
  Grid
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useBooks } from '../../context/BookContext';
import UploadFileIcon from '@mui/icons-material/UploadFile';

const Dashboard = () => {
  const navigate = useNavigate();
  const { books, addBook, setCurrentBook } = useBooks();
  const [isUploading, setIsUploading] = React.useState(false);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    try {
      setIsUploading(true);
      const bookId = await addBook({ file });
      const uploadedBook = books.find(b => b.id === bookId);
      if (uploadedBook) {
        setCurrentBook(uploadedBook);
        navigate('/synopsis');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" sx={{ mb: 4 }}>
        Dashboard
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper 
            sx={{ 
              p: 4, 
              textAlign: 'center',
              border: '2px dashed #E0E0E0',
              backgroundColor: '#FFFFFF'
            }}
          >
            <input
              type="file"
              accept=".epub,.pdf,.doc,.docx"
              style={{ display: 'none' }}
              id="file-upload"
              onChange={handleFileUpload}
            />
            <label htmlFor="file-upload">
              <Button
                component="span"
                variant="contained"
                size="large"
                disabled={isUploading}
                startIcon={<UploadFileIcon />}
                sx={{ minWidth: 200 }}
              >
                {isUploading ? 'Caricamento...' : 'Carica libro'}
              </Button>
            </label>
            <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
              Supporta file .epub, .pdf, .doc, .docx
            </Typography>
          </Paper>
        </Grid>

        {books.length > 0 && (
          <Grid item xs={12}>
            <Typography variant="h6" sx={{ mb: 2, mt: 2 }}>
              Libri recenti
            </Typography>
            <Grid container spacing={2}>
              {books.map((book) => (
                <Grid item xs={12} sm={6} md={4} key={book.id}>
                  <Paper 
                    sx={{ 
                      p: 2, 
                      cursor: 'pointer',
                      '&:hover': { backgroundColor: '#F8F9FA' }
                    }}
                    onClick={() => {
                      setCurrentBook(book);
                      navigate('/synopsis');
                    }}
                  >
                    <Typography variant="subtitle1" noWrap>
                      {book.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {book.date}
                    </Typography>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default Dashboard;
