import React from 'react';
import TaskCard from './TaskCard';
import { Box, Typography, Button, Paper, alpha } from '@mui/material';
import ImageIcon from '@mui/icons-material/Image';
import FileDownloadIcon from '@mui/icons-material/FileDownload';

const CoverTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  navigate,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={ImageIcon}
      title="Crea un'immagine di copertina"
      description="Sceglierò delle scene basandomi sul tuo manoscritto che ne rappresentino bene il contenuto e l'atmosfera. Dopodiché creerò una immagine di copertina per ognuna che potrai modificare, rigenerare completamente e scaricare in vari formati."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA COPERTINE"
      generateButtonStyle={generateButtonStyle}
    >
      {currentBook?.metadata?.covers?.scenes?.map((scene, index) => (
        <Paper
          key={index}
          sx={{
            p: 3,
            mb: 2,
            backgroundColor: 'background.paper',
            '&:hover': {
              boxShadow: '0px 8px 24px rgba(0, 0, 0, 0.08)',
            }
          }}
        >
          <Box sx={{ display: 'flex', gap: 3 }}>
            {/* Cover Preview */}
            <Box
              sx={{
                width: 180,
                height: 270,
                backgroundColor: alpha('#4B6BFB', 0.1),
                borderRadius: 2,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                overflow: 'hidden'
              }}
            >
              {currentBook?.metadata?.covers?.images?.[index]?.imageUrl ? (
                <img 
                  src={currentBook.metadata.covers.images[index].imageUrl}
                  alt={`Cover ${index + 1}`}
                  style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                />
              ) : (
                <ImageIcon sx={{ fontSize: 48, color: 'primary.main', opacity: 0.5 }} />
              )}
            </Box>

            {/* Content */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" gutterBottom>
                {scene?.title || ''}
              </Typography>
              <Typography variant="body2" color="text.secondary" paragraph>
                {scene?.description || ''}
              </Typography>

              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <Button
                  variant="outlined"
                  size="small"
                  startIcon={<FileDownloadIcon />}
                  onClick={() => navigate('/cover-selection', { 
                    state: { 
                      bookId: currentBook.id,
                      scene: scene,
                      index: index,
                      imageUrl: currentBook?.metadata?.covers?.images?.[index]?.imageUrl
                    }
                  })}
                  disabled={!currentBook?.metadata?.covers?.images?.[index]?.imageUrl}
                >
                  VEDI COPERTINA
                </Button>
              </Box>
            </Box>
          </Box>
        </Paper>
      ))}
    </TaskCard>
  );
};

export default CoverTaskCard;
