import React from 'react';
import TaskCard from './TaskCard';
import { Box, Typography, Paper, IconButton, CircularProgress } from '@mui/material';
import CategoryIcon from '@mui/icons-material/Category';
import RefreshIcon from '@mui/icons-material/Refresh';
import { alpha } from '@mui/material/styles';

const CategoryTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={CategoryIcon}
      title="Genera le categorie"
      description="Analizzerò il tuo libro e suggerirò le categorie BISAC più appropriate per migliorare la visibilità sugli store."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA CATEGORIE"
      generateButtonStyle={generateButtonStyle}
      allowRegenerate={false}
    >
      {/* Categoria Principale */}
      <Paper sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Typography variant="subtitle2" color="primary" sx={{ flex: 1 }}>
            Categoria Principale
          </Typography>
          <IconButton 
            size="small"
            onClick={() => onRegenerateCategory('main')}
            disabled={isGeneratingCategory}
          >
            {isGeneratingCategory ? 
              <CircularProgress size={20} /> : 
              <RefreshIcon />
            }
          </IconButton>
        </Box>
        <Typography variant="body2">
          {currentBook.metadata?.categories?.mainCategory || ''}
        </Typography>
      </Paper>

      {/* Categorie Secondarie */}
      {currentBook.metadata?.categories?.secondaryCategories?.map((category, index) => (
        <Paper key={index} sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Typography variant="subtitle2" color="primary" sx={{ flex: 1 }}>
              Categoria Secondaria {index + 1}
            </Typography>
            <IconButton 
              size="small"
              onClick={() => onRegenerateCategory(`secondary${index + 1}`)}
              disabled={isGeneratingCategory}
            >
              {isGeneratingCategory ? 
                <CircularProgress size={20} /> : 
                <RefreshIcon />
              }
            </IconButton>
          </Box>
          <Typography variant="body2">
            {category}
          </Typography>
        </Paper>
      ))}
    </TaskCard>
  );
};

export default CategoryTaskCard;
