import React, { createContext, useState, useContext } from 'react';

const BookContext = createContext();

export const BookProvider = ({ children }) => {
  const [books, setBooks] = useState([
    { id: 1, title: 'Il patto', date: 'NOVEMBER, 2024' }
  ]);

  const addBook = (book) => {
    setBooks([...books, {
      id: books.length + 1,
      title: book.name,
      date: new Date().toLocaleString('en-US', { month: 'long', year: 'numeric' }).toUpperCase()
    }]);
  };

  const removeBook = (bookId) => {
    setBooks(books.filter(book => book.id !== bookId));
  };

  const updateBook = (bookId, updatedBook) => {
    setBooks(books.map(book => 
      book.id === bookId ? updatedBook : book
    ));
  };

  return (
    <BookContext.Provider value={{ books, addBook, removeBook, updateBook }}>
      {children}
    </BookContext.Provider>
  );
};

export const useBooks = () => useContext(BookContext);
