import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Container,
  Paper,
} from '@mui/material';
import { useBooks } from '../../context/BookContext';
import UploadSection from './UploadSection';
import NotificationSnackbar from '../common/NotificationSnackbar';

/**
 * MainDashboard - Componente principale della dashboard
 * Gestisce il caricamento dei libri e la navigazione post-upload
 */
const MainDashboard = () => {
  const navigate = useNavigate();
  const { addBook, isUploading } = useBooks();
  // Stati per gestire messaggi di successo ed errore
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  /**
   * Gestisce il processo di upload del file
   * @param {File} file - Il file da caricare
   */
  const handleUpload = async (file) => {
    if (!file) return;

    // Validazione dell'estensione del file
    const validTypes = ['.pdf', '.doc', '.docx', '.txt'];
    const fileExtension = file.name.substring(file.name.lastIndexOf('.'));
    if (!validTypes.includes(fileExtension.toLowerCase())) {
      setError('Formato file non supportato. Usa PDF, DOC, DOCX o TXT.');
      return;
    }

    try {
      setError(null);
      const bookId = await addBook({ file });
      setSuccess('Libro caricato con successo');
      // Reindirizza alla pagina dei task dopo il caricamento
      navigate('/tasks', { 
        state: { 
          bookName: file.name.replace(/\.[^/.]+$/, "") 
        }
      });
    } catch (error) {
      setError(error.message || 'Errore durante il caricamento. Riprova.');
      console.error('Upload error:', error);
    }
  };

  // Reset degli stati delle notifiche
  const handleCloseSnackbar = () => {
    setSuccess(null);
    setError(null);
  };

  return (
    <div className="p-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Dashboard</h1>
        <p className="text-gray-600">Welcome back! Here's your overview.</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        {/* Statistiche Cards */}
        <div className="bg-white rounded-xl shadow-sm p-6 border border-gray-100">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-800">Total Tasks</h3>
            <span className="p-2 bg-blue-100 rounded-lg">
              <ChartIcon className="h-6 w-6 text-blue-600" />
            </span>
          </div>
          <p className="text-3xl font-bold text-gray-900">24</p>
          <p className="text-sm text-gray-500 mt-2">+12% from last month</p>
        </div>
        {/* Ripeti per altre statistiche */}
      </div>

      <div className="bg-white rounded-xl shadow-sm p-6 border border-gray-100">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold text-gray-800">Recent Tasks</h2>
          <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-300">
            Add New Task
          </button>
        </div>
        <div className="space-y-4">
          {/* TaskCards verranno renderizzati qui */}
        </div>
      </div>
    </div>
  );
};

export default MainDashboard;
