import React from 'react';
import {
  Box,
  Typography,
  InputBase,
  Paper,
  IconButton,
  List,
  ListItem,
  ListItemText
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';

const Sidebar = () => {
  return (
    <Box
      sx={{
        width: 280,
        height: '100vh',
        borderRight: '1px solid #E0E0E0',
        backgroundColor: '#FFFFFF',
        p: 3,
      }}
    >
      <Typography variant="h6" sx={{ mb: 3 }}>
        Your tasks
      </Typography>

      <Paper
        sx={{
          p: '2px 4px',
          display: 'flex',
          alignItems: 'center',
          mb: 3,
          boxShadow: 'none',
          border: '1px solid #E0E0E0',
        }}
      >
        <InputBase
          sx={{ ml: 1, flex: 1 }}
          placeholder="search something..."
        />
        <IconButton type="button" sx={{ p: '10px' }} aria-label="search">
          <SearchIcon />
        </IconButton>
      </Paper>

      <Typography 
        variant="subtitle2" 
        color="text.secondary"
        sx={{ mb: 2, mt: 3 }}
      >
        NOVEMBER, 2024
      </Typography>

      <List sx={{ p: 0 }}>
        <ListItem
          sx={{
            backgroundColor: '#F8F9FF',
            borderRadius: '8px',
            mb: 1,
            '&:hover': {
              backgroundColor: '#F8F9FF',
            }
          }}
          secondaryAction={
            <IconButton edge="end">
              <MoreVertIcon />
            </IconButton>
          }
        >
          <InsertDriveFileIcon 
            sx={{ 
              mr: 2, 
              color: 'primary.main',
              fontSize: 20
            }} 
          />
          <ListItemText 
            primary="Il patto"
            primaryTypographyProps={{
              variant: 'body2',
              fontWeight: 500
            }}
          />
        </ListItem>
      </List>
    </Box>
  );
};

export default Sidebar;
