import React from 'react';
import TaskCard from './TaskCard';
import { Typography } from '@mui/material';
import MenuBookIcon from '@mui/icons-material/MenuBook';

const PrefaceTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  onRegenerate,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={MenuBookIcon}
      title="Crea la prefazione"
      description="GenererÃ² una prefazione dettagliata ed esaustiva che introduca il lettore ai temi principali del libro, al contesto e al messaggio che vuoi trasmettere."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA PREFAZIONE"
      onRegenerate={onRegenerate}
      generateButtonStyle={generateButtonStyle}
    >
      <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', lineHeight: 1.8 }}>
        {currentBook.metadata?.preface?.preface || ''}
      </Typography>
    </TaskCard>
  );
};

export default PrefaceTaskCard;
