import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  InputBase,
  Paper,
  IconButton,
  List,
  ListItem,
  ListItemText,
  Divider
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import { useBooks } from '../../context/BookContext';

const Sidebar = () => {
  const { books } = useBooks();
  const navigate = useNavigate();
  const location = useLocation();
  const [searchTerm, setSearchTerm] = React.useState('');

  const filteredBooks = books.filter(book => 
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Raggruppa i libri per data
  const groupedBooks = filteredBooks.reduce((acc, book) => {
    if (!acc[book.date]) {
      acc[book.date] = [];
    }
    acc[book.date].push(book);
    return acc;
  }, {});

  return (
    <Box
      sx={{
        width: 280,
        height: '100vh',
        borderRight: '1px solid #E0E0E0',
        backgroundColor: '#FFFFFF',
        p: 3,
        position: 'fixed',
        left: 0,
        top: 0,
        overflowY: 'auto'
      }}
    >
      <Typography variant="h6" sx={{ mb: 3 }}>
        Your tasks
      </Typography>

      <Paper
        sx={{
          p: '2px 4px',
          display: 'flex',
          alignItems: 'center',
          mb: 3,
          boxShadow: 'none',
          border: '1px solid #E0E0E0',
        }}
      >
        <InputBase
          sx={{ ml: 1, flex: 1 }}
          placeholder="search something..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <IconButton type="button" sx={{ p: '10px' }} aria-label="search">
          <SearchIcon />
        </IconButton>
      </Paper>

      {Object.entries(groupedBooks).map(([date, dateBooks]) => (
        <Box key={date}>
          <Typography 
            variant="subtitle2" 
            color="text.secondary"
            sx={{ mb: 2, mt: 3 }}
          >
            {date}
          </Typography>

          <List sx={{ p: 0 }}>
            {dateBooks.map((book) => (
              <ListItem
                key={book.id}
                sx={{
                  backgroundColor: location.pathname === '/tasks' && 
                                 location.state?.bookName === book.title ? 
                                 '#F8F9FF' : 'transparent',
                  borderRadius: '8px',
                  mb: 1,
                  cursor: 'pointer',
                  '&:hover': {
                    backgroundColor: '#F8F9FF',
                  }
                }}
                onClick={() => navigate('/tasks', { state: { bookName: book.title } })}
                secondaryAction={
                  <IconButton edge="end">
                    <MoreVertIcon />
                  </IconButton>
                }
              >
                <InsertDriveFileIcon 
                  sx={{ 
                    mr: 2, 
                    color: 'primary.main',
                    fontSize: 20
                  }} 
                />
                <ListItemText 
                  primary={book.title}
                  primaryTypographyProps={{
                    variant: 'body2',
                    fontWeight: 500
                  }}
                />
              </ListItem>
            ))}
          </List>
        </Box>
      ))}
    </Box>
  );
};

export default Sidebar;
