import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Container,
  Paper,
  IconButton,
  CircularProgress
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { useBooks } from '../../context/BookContext';

const MainDashboard = () => {
  const navigate = useNavigate();
  const fileInputRef = React.useRef();
  const { addBook, isUploading } = useBooks();
  const [error, setError] = useState(null);

  const handleUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      try {
        setError(null);
        const bookId = await addBook({ file });
        navigate('/tasks', { 
          state: { 
            bookName: file.name.replace(/\.[^/.]+$/, "") 
          }
        });
      } catch (error) {
        setError('Error uploading file. Please try again.');
        console.error('Upload error:', error);
      }
    }
  };

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4, textAlign: 'center' }}>
        <Typography variant="h2" component="h1" gutterBottom>
          Benvenuto nel tuo Publishing Dashboard
        </Typography>
        <Typography variant="h5" color="text.secondary" gutterBottom>
          Carica il tuo libro per iniziare a ottimizzare la tua presenza online
        </Typography>

        <Paper
          sx={{
            mt: 4,
            p: 6,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            backgroundColor: 'background.paper',
          }}
        >
          <Box
            sx={{
              mb: 3,
              width: '48px',
              height: '48px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <IconButton
              sx={{
                color: 'primary.main',
                '&:hover': { backgroundColor: 'transparent' },
              }}
              disableRipple
              disabled={isUploading}
            >
              {isUploading ? (
                <CircularProgress size={48} color="primary" />
              ) : (
                <CloudUploadIcon sx={{ fontSize: 48 }} />
              )}
            </IconButton>
          </Box>

          <input
            type="file"
            ref={fileInputRef}
            style={{ display: 'none' }}
            onChange={handleUpload}
            accept=".pdf,.doc,.docx,.txt"
            disabled={isUploading}
          />

          <Button
            variant="contained"
            color="primary"
            size="large"
            startIcon={<CloudUploadIcon />}
            onClick={handleButtonClick}
            disabled={isUploading}
          >
            {isUploading ? 'CARICAMENTO...' : 'CARICA IL TUO LIBRO'}
          </Button>

          {error && (
            <Typography color="error" sx={{ mt: 2 }}>
              {error}
            </Typography>
          )}
        </Paper>
      </Box>
    </Container>
  );
};

export default MainDashboard;
