import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Container,
  Paper,
  Grid,
  IconButton,
  Collapse
} from '@mui/material';
import MetadataIcon from '@mui/icons-material/Description';
import CoverIcon from '@mui/icons-material/Image';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import CategoryIcon from '@mui/icons-material/Category';
import KeyIcon from '@mui/icons-material/Key';
import { useBooks } from '../../context/BookContext';

const TaskDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { books } = useBooks();
  const [currentBook, setCurrentBook] = useState(null);
  const [expandedCard, setExpandedCard] = useState(null);

  useEffect(() => {
    const bookName = location.state?.bookName;
    if (bookName) {
      const book = books.find(b => b.title === bookName);
      if (book) {
        setCurrentBook(book);
      } else {
        navigate('/');
      }
    }
  }, [location.state, books, navigate]);

  const handleExpand = (cardId) => {
    setExpandedCard(expandedCard === cardId ? null : cardId);
  };

  const handleGenerateCategories = () => {
    console.log('Generating categories...');
  };

  const handleGenerateKeywords = () => {
    console.log('Generating keywords...');
  };

  if (!currentBook) return null;

  return (
    <Box sx={{ backgroundColor: '#F8F9FA', minHeight: '100vh' }}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <IconButton 
            onClick={() => navigate('/')}
            sx={{ 
              mr: 2,
              color: 'text.secondary',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.04)'
              }
            }}
          >
            <ArrowBackIcon />
          </IconButton>
          <Box>
            <Typography 
              variant="subtitle2" 
              color="text.secondary"
              sx={{ mb: 1 }}
            >
              {currentBook.date}
            </Typography>
            <Typography 
              variant="h5" 
              component="h1"
              sx={{ 
                fontWeight: 500,
                color: 'text.primary'
              }}
            >
              {currentBook.title}
            </Typography>
          </Box>
        </Box>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Paper 
              sx={{ 
                p: 3,
                '&:hover': {
                  boxShadow: '0px 8px 24px rgba(0, 0, 0, 0.08)',
                }
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box 
                  sx={{ 
                    mr: 3,
                    backgroundColor: '#F8F9FF',
                    borderRadius: '12px',
                    p: 2
                  }}
                >
                  <MetadataIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Crea e ottimizza i metadati
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Ti suggerirò i metadati più adatti per migliorare la visibilità organica e le vendite sugli store.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('metadata')}
                    endIcon={expandedCard === 'metadata' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'metadata'} timeout="auto" unmountOnExit>
                    <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
                      <Button
                        variant="contained"
                        color="primary"
                        startIcon={<CategoryIcon />}
                        onClick={handleGenerateCategories}
                      >
                        Genera Categorie
                      </Button>
                      <Button
                        variant="contained"
                        color="primary"
                        startIcon={<KeyIcon />}
                        onClick={handleGenerateKeywords}
                      >
                        Genera Keywords
                      </Button>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper 
              sx={{ 
                p: 3,
                '&:hover': {
                  boxShadow: '0px 8px 24px rgba(0, 0, 0, 0.08)',
                }
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box 
                  sx={{ 
                    mr: 3,
                    backgroundColor: '#F8F9FF',
                    borderRadius: '12px',
                    p: 2
                  }}
                >
                  <CoverIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Create a cover image
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    I'll choose scenes based on your manuscript that effectively represent its content. I'll then generate cover images for each scene, which you can modify, request additional ones, and download in various formats.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('cover')}
                    endIcon={expandedCard === 'cover' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'cover'} timeout="auto" unmountOnExit>
                    <Box sx={{ mt: 3 }}>
                      {/* Add cover image functionality here */}
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>
        </Grid>

        <Box sx={{ mt: 4, textAlign: 'center' }}>
          <Typography variant="body2" color="text.secondary">
            © 2024 Marco Luigi Palma - All rights reserved.
          </Typography>
          <Box sx={{ mt: 1 }}>
            <Button color="primary" size="small">View Privacy Policy</Button>
            <Button color="primary" size="small">Terms & Conditions</Button>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default TaskDashboard;
