import React from 'react';
import { Box } from '@mui/material';
import Sidebar from './Sidebar';

const Layout = ({ children }) => {
  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box 
        component="main" 
        sx={{ 
          flexGrow: 1, 
          marginLeft: '280px',
          minHeight: '100vh',
          backgroundColor: '#F8F9FA'
        }}
      >
        {children}
      </Box>
    </Box>
  );
};

export default Layout;
