import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import DescriptionIcon from '@mui/icons-material/Description';
import CategoryIcon from '@mui/icons-material/Category';
import ImageIcon from '@mui/icons-material/Image';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import StoreIcon from '@mui/icons-material/Store';
import KeyIcon from '@mui/icons-material/Key';
import AutoStoriesIcon from '@mui/icons-material/AutoStories';

const Sidebar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const bookId = location.pathname.split('/')[2]; // Estrae l'ID del libro dall'URL

  const bookSections = [
    { 
      title: 'Sinossi', 
      path: `/book/${bookId}/synopsis`,
      icon: <DescriptionIcon />
    },
    { 
      title: 'Categorie', 
      path: `/book/${bookId}/categories`,
      icon: <CategoryIcon />
    },
    { 
      title: 'Copertina', 
      path: `/book/${bookId}/cover`,
      icon: <ImageIcon />
    },
    { 
      title: 'Quarta di copertina', 
      path: `/book/${bookId}/backcover`,
      icon: <MenuBookIcon />
    },
    { 
      title: 'Prefazione', 
      path: `/book/${bookId}/preface`,
      icon: <AutoStoriesIcon />
    },
    { 
      title: 'Descrizione Store', 
      path: `/book/${bookId}/store`,
      icon: <StoreIcon />
    },
    { 
      title: 'Parole chiave', 
      path: `/book/${bookId}/keywords`,
      icon: <KeyIcon />
    }
  ];

  return (
    <Box
      sx={{
        width: 280,
        height: '100vh',
        borderRight: '1px solid #E0E0E0',
        backgroundColor: '#FFFFFF',
        p: 3,
        position: 'fixed',
        left: 0,
        top: 0,
        overflowY: 'auto'
      }}
    >
      <Typography variant="h6" sx={{ mb: 3 }}>
        Book Editor
      </Typography>

      <List>
        {/* Dashboard Button */}
        <ListItem disablePadding>
          <ListItemButton
            onClick={() => navigate('/')}
            selected={location.pathname === '/'}
            sx={{
              borderRadius: '8px',
              mb: 1,
              '&.Mui-selected': {
                backgroundColor: 'primary.lighter',
                '&:hover': {
                  backgroundColor: 'primary.lighter'
                }
              }
            }}
          >
            <ListItemIcon>
              <DashboardIcon />
            </ListItemIcon>
            <ListItemText 
              primary="Dashboard"
              primaryTypographyProps={{
                variant: 'body2',
                fontWeight: location.pathname === '/' ? 600 : 400
              }}
            />
          </ListItemButton>
        </ListItem>

        {/* Sections Divider */}
        {bookId && (
          <>
            <Typography 
              variant="subtitle2" 
              color="text.secondary" 
              sx={{ px: 2, py: 1, mt: 2 }}
            >
              Sezioni
            </Typography>

            {/* Book Sections */}
            {bookSections.map((section) => (
              <ListItem key={section.title} disablePadding>
                <ListItemButton
                  onClick={() => navigate(section.path)}
                  selected={location.pathname === section.path}
                  sx={{
                    borderRadius: '8px',
                    mb: 0.5,
                    '&.Mui-selected': {
                      backgroundColor: 'primary.lighter',
                      '&:hover': {
                        backgroundColor: 'primary.lighter'
                      }
                    }
                  }}
                >
                  <ListItemIcon>
                    {section.icon}
                  </ListItemIcon>
                  <ListItemText 
                    primary={section.title}
                    primaryTypographyProps={{
                      variant: 'body2',
                      fontWeight: location.pathname === section.path ? 600 : 400
                    }}
                  />
                </ListItemButton>
              </ListItem>
            ))}
          </>
        )}
      </List>
    </Box>
  );
};

export default Sidebar;
