import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Container,
  Grid,
  IconButton,
  Alert,
  Snackbar
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useBooks } from '../../context/BookContext';

// Import dei componenti TaskCard
import CategoryTaskCard from './CategoryTaskCard';
import CoverTaskCard from './CoverTaskCard';
import BackCoverTaskCard from './BackCoverTaskCard';
import PrefaceTaskCard from './PrefaceTaskCard';
import StoreDescriptionTaskCard from './StoreDescriptionTaskCard';
import KeywordsTaskCard from './KeywordsTaskCard';

/**
 * TaskDashboard - Componente principale per la gestione dei metadati del libro
 * Permette di generare e gestire:
 * - Categorie BISAC
 * - Copertine
 * - Quarta di copertina
 * - Prefazione
 * - Descrizione per gli store
 */
const TaskDashboard = () => {
  // Hook per la navigazione e l'accesso ai parametri della route
  const location = useLocation();
  const navigate = useNavigate();

  // Accesso al contesto dei libri e relative funzioni
  const { 
    books, 
    updateBookMetadata, 
    generateCategories, 
    generateScenes, 
    generateCoverImages, 
    generateBackCover, 
    generatePreface, 
    generateStoreDescription, 
    generateKeywords
  } = useBooks();

  // Stati locali per gestire il libro corrente e le varie sezioni
  const [currentBook, setCurrentBook] = useState(null);
  const [expandedCard, setExpandedCard] = useState(null);

  // Stati per controllare la visibilità delle sezioni
  const [showCategories, setShowCategories] = useState(false);
  const [showCovers, setShowCovers] = useState(false);
  const [showBackCover, setShowBackCover] = useState(false);
  const [showPreface, setShowPreface] = useState(false);
  const [showStoreDescription, setShowStoreDescription] = useState(false);
  const [showKeywords, setShowKeywords] = useState(false);

  // Stati per gestire i processi di generazione
  const [isGenerating, setIsGenerating] = useState(false);
  const [isGeneratingCovers, setIsGeneratingCovers] = useState(false);
  const [isGeneratingBackCover, setIsGeneratingBackCover] = useState(false);
  const [isGeneratingPreface, setIsGeneratingPreface] = useState(false);
  const [isGeneratingStoreDescription, setIsGeneratingStoreDescription] = useState(false);
  const [isGeneratingKeywords, setIsGeneratingKeywords] = useState(false);

  // Stati per la gestione degli errori e dei messaggi di successo
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  /**
   * Effetto per inizializzare il componente con i dati del libro selezionato
   * e impostare la visibilità delle sezioni in base ai metadati esistenti
   */
  useEffect(() => {
    const bookName = location.state?.bookName;
    if (bookName) {
      const book = books.find(b => b.title === bookName);
      if (book) {
        setCurrentBook(book);
        setShowCategories(!!book.metadata?.categories);
        setShowCovers(!!book.metadata?.covers?.scenes?.length);
        setShowBackCover(!!book.metadata?.backCover);
        setShowPreface(!!book.metadata?.preface);
        setShowStoreDescription(!!book.metadata?.storeDescription);
        setShowKeywords(!!book.metadata?.keywords);
      }
    }
  }, [location.state, books]);

  /**
   * Gestisce l'espansione/collasso delle sezioni
   * @param {string} cardId - ID della sezione da espandere/collassare
   */
  const handleExpand = (cardId) => {
    if (expandedCard === cardId) {
      setExpandedCard(null);
    } else {
      setExpandedCard(cardId);
    }
  };

  /**
   * Gestisce la generazione delle categorie BISAC
   * Aggiorna i metadati del libro con le nuove categorie generate
   */
  const handleGenerateCategories = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGenerating(true);

      const result = await generateCategories(currentBook.id);
      await updateBookMetadata(currentBook.id, {
        categories: result
      });

      setShowCategories(true);
      setSuccess('Categorie generate con successo');
    } catch (error) {
      setError('Errore nella generazione delle categorie. Riprova.');
      setShowCategories(false);
    } finally {
      setIsGenerating(false);
    }
  };

  // Stile comune per i pulsanti di generazione
  const generateButtonStyle = {
    minWidth: '200px',
    maxWidth: '300px',
    height: '48px',
    borderRadius: '8px',
    textTransform: 'uppercase',
    fontSize: '1rem',
    fontWeight: 500,
    boxShadow: '0 4px 12px rgba(75, 107, 251, 0.2)',
    '&:hover': {
      boxShadow: '0 6px 16px rgba(75, 107, 251, 0.3)',
      backgroundColor: 'primary.dark'
    }
  };

  // Rendering condizionale se non c'è un libro selezionato
  if (!currentBook) return null;

  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(null);
  };

  const handleGenerateCovers = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGeneratingCovers(true);

      // Prima generiamo le scene
      const scenesResponse = await generateScenes(currentBook.id);
      console.log('Scene generate:', scenesResponse);

      if (!scenesResponse || !scenesResponse.scenes || !Array.isArray(scenesResponse.scenes)) {
        throw new Error('Invalid scenes format received from server');
      }

      // Prepariamo le scene per la generazione delle immagini
      const scenes = scenesResponse.scenes.map(scene => ({
        title: scene.title,
        description: scene.description
      }));

      // Poi generiamo le copertine usando le scene
      const coversResponse = await generateCoverImages(currentBook.id, scenes);

      if (!coversResponse || !coversResponse.covers) {
        throw new Error('Invalid covers response from server');
      }

      // Aggiorniamo i metadata del libro
      await updateBookMetadata(currentBook.id, {
        covers: {
          scenes: scenesResponse.scenes,
          images: coversResponse.covers
        }
      });

      setShowCovers(true);
      setSuccess('Copertine generate con successo');
    } catch (error) {
      console.error('Errore nella generazione delle copertine:', error);
      setError('Errore nella generazione delle copertine. Riprova.');
      setShowCovers(false);
    } finally {
      setIsGeneratingCovers(false);
    }
  };

  const handleGenerateBackCover = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGeneratingBackCover(true);

      const result = await generateBackCover(currentBook.id);

      await updateBookMetadata(currentBook.id, {
        backCover: result
      });

      setShowBackCover(true);
      setSuccess('Quarta di copertina generata con successo');
    } catch (error) {
      setError('Errore nella generazione della quarta di copertina. Riprova.');
      setShowBackCover(false);
    } finally {
      setIsGeneratingBackCover(false);
    }
  };

  const handleGeneratePreface = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGeneratingPreface(true);

      const result = await generatePreface(currentBook.id);

      await updateBookMetadata(currentBook.id, {
        preface: result
      });

      setShowPreface(true);
      setSuccess('Prefazione generata con successo');
    } catch (error) {
      setError('Errore nella generazione della prefazione. Riprova.');
      setShowPreface(false);
    } finally {
      setIsGeneratingPreface(false);
    }
  };

  const handleGenerateStoreDescription = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGeneratingStoreDescription(true);

      const result = await generateStoreDescription(currentBook.id);

      await updateBookMetadata(currentBook.id, {
        storeDescription: result
      });

      setShowStoreDescription(true);
      setSuccess('Descrizione per gli store generata con successo');
    } catch (error) {
      setError('Errore nella generazione della descrizione per gli store. Riprova.');
      setShowStoreDescription(false);
    } finally {
      setIsGeneratingStoreDescription(false);
    }
  };

  const handleGenerateKeywords = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGeneratingKeywords(true);

      const result = await generateKeywords(currentBook.id);

      await updateBookMetadata(currentBook.id, {
        keywords: result
      });

      setShowKeywords(true);
      setSuccess('Parole chiave generate con successo');
    } catch (error) {
      setError('Errore nella generazione delle parole chiave. Riprova.');
      setShowKeywords(false);
    } finally {
      setIsGeneratingKeywords(false);
    }
  };

  return (
    <Box sx={{ backgroundColor: '#F8F9FA', minHeight: '100vh' }}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        {/* Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <IconButton 
            onClick={() => navigate('/')}
            sx={{ 
              mr: 2,
              color: 'text.secondary',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.04)'
              }
            }}
          >
            <ArrowBackIcon />
          </IconButton>
          <Box sx={{ flex: 1 }}>
            <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>
              {currentBook.date}
            </Typography>
            <Typography variant="h5" component="h1" sx={{ fontWeight: 500, color: 'text.primary' }}>
              {currentBook.title}
            </Typography>
          </Box>
        </Box>

        {/* Task Cards */}
        <Grid container spacing={3}>
          {/* Categorie */}
          <Grid item xs={12}>
            <CategoryTaskCard
              currentBook={currentBook}
              isExpanded={expandedCard === 'metadata'}
              onExpand={() => handleExpand('metadata')}
              isGenerating={isGenerating}
              onGenerate={handleGenerateCategories}
              showContent={showCategories}
              generateButtonStyle={generateButtonStyle}
            />
          </Grid>

          {/* Copertine */}
          <Grid item xs={12}>
            <CoverTaskCard
              currentBook={currentBook}
              isExpanded={expandedCard === 'cover'}
              onExpand={() => handleExpand('cover')}
              isGenerating={isGeneratingCovers}
              onGenerate={handleGenerateCovers}
              showContent={showCovers}
              navigate={navigate}
              generateButtonStyle={generateButtonStyle}
            />
          </Grid>

          {/* Quarta di copertina */}
          <Grid item xs={12}>
            <BackCoverTaskCard
              currentBook={currentBook}
              isExpanded={expandedCard === 'backcover'}
              onExpand={() => handleExpand('backcover')}
              isGenerating={isGeneratingBackCover}
              onGenerate={handleGenerateBackCover}
              showContent={showBackCover}
              generateButtonStyle={generateButtonStyle}
            />
          </Grid>

          {/* Prefazione */}
          <Grid item xs={12}>
            <PrefaceTaskCard
              currentBook={currentBook}
              isExpanded={expandedCard === 'preface'}
              onExpand={() => handleExpand('preface')}
              isGenerating={isGeneratingPreface}
              onGenerate={handleGeneratePreface}
              showContent={showPreface}
              generateButtonStyle={generateButtonStyle}
            />
          </Grid>

          {/* Descrizione Store */}
          <Grid item xs={12}>
            <StoreDescriptionTaskCard
              currentBook={currentBook}
              isExpanded={expandedCard === 'store'}
              onExpand={() => handleExpand('store')}
              isGenerating={isGeneratingStoreDescription}
              onGenerate={handleGenerateStoreDescription}
              showContent={showStoreDescription}
              generateButtonStyle={generateButtonStyle}
            />
          </Grid>

          {/* Keywords Card */}
          <Grid item xs={12}>
            <KeywordsTaskCard
              currentBook={currentBook}
              isExpanded={expandedCard === 'keywords'}
              onExpand={() => handleExpand('keywords')}
              isGenerating={isGeneratingKeywords}
              onGenerate={handleGenerateKeywords}
              showContent={showKeywords}
              generateButtonStyle={generateButtonStyle}
            />
          </Grid>
        </Grid>

        {/* Footer */}
        <Box sx={{ mt: 4, textAlign: 'center' }}>
          <Typography variant="body2" color="text.secondary">
            © 2024 Marco Luigi Palma - All rights reserved.
          </Typography>
          <Box sx={{ mt: 1 }}>
            <Button color="primary" size="small">View Privacy Policy</Button>
            <Button color="primary" size="small">Terms & Conditions</Button>
          </Box>
        </Box>

        {/* Snackbars */}
        <Snackbar 
          open={!!error} 
          autoHideDuration={6000} 
          onClose={handleCloseSnackbar}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
            {error}
          </Alert>
        </Snackbar>

        <Snackbar 
          open={!!success} 
          autoHideDuration={3000} 
          onClose={handleCloseSnackbar}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
            {success}
          </Alert>
        </Snackbar>
      </Container>
    </Box>
  );
};

export default TaskDashboard;
