import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Container,
  Paper,
  IconButton,
  Button,
  Grid,
  CircularProgress,
  Alert,
  Snackbar
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import RefreshIcon from '@mui/icons-material/Refresh';
import DownloadIcon from '@mui/icons-material/Download';
import { useBooks } from '../../context/BookContext';

const CoverSelectionDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { generateCoverImages } = useBooks();
  const [covers, setCovers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const bookId = location.state?.bookId;
  const scene = location.state?.scene;

  const generateCovers = async () => {
    try {
      setLoading(true);
      setError(null);
      const result = await generateCoverImages(bookId, [scene]);
      if (result && result.covers) {
        setCovers(result.covers);
        setSuccess('Copertine generate con successo');
      } else {
        throw new Error('Failed to generate covers');
      }
    } catch (error) {
      console.error('Error generating covers:', error);
      setError('Errore nella generazione delle copertine');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (bookId && scene && covers.length === 0) {
      generateCovers();
    } else if (!bookId || !scene) {
      navigate('/tasks');
    }
  }, [bookId, scene]);

  const handleDownload = (imageUrl) => {
    const link = document.createElement('a');
    link.href = imageUrl;
    link.download = 'cover.png';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(null);
  };

  if (!bookId || !scene) {
    navigate('/tasks');
    return null;
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
        <IconButton 
          onClick={() => navigate(-1)}
          sx={{ mr: 2 }}
        >
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h5">
          {scene.title}
        </Typography>
      </Box>

      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="body1" paragraph>
          {scene.description}
        </Typography>
      </Paper>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={3}>
          {covers.map((cover, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Paper sx={{ p: 2 }}>
                <Box sx={{ position: 'relative' }}>
                  <img 
                    src={cover.imageUrl} 
                    alt={`Cover ${index + 1}`}
                    style={{ width: '100%', height: 'auto', borderRadius: '8px' }}
                  />
                  <Box sx={{ 
                    position: 'absolute', 
                    top: 8, 
                    right: 8,
                    display: 'flex',
                    gap: 1
                  }}>
                    <IconButton 
                      onClick={() => handleDownload(cover.imageUrl)}
                      sx={{ backgroundColor: 'white' }}
                    >
                      <DownloadIcon />
                    </IconButton>
                    <IconButton 
                      onClick={generateCovers}
                      sx={{ backgroundColor: 'white' }}
                    >
                      <RefreshIcon />
                    </IconButton>
                  </Box>
                </Box>
              </Paper>
            </Grid>
          ))}
        </Grid>
      )}

      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
      >
        <Alert onClose={handleCloseSnackbar} severity="error">
          {error}
        </Alert>
      </Snackbar>

      <Snackbar 
        open={!!success} 
        autoHideDuration={3000} 
        onClose={handleCloseSnackbar}
      >
        <Alert onClose={handleCloseSnackbar} severity="success">
          {success}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default CoverSelectionDashboard;
