import React from 'react';
import { Box, Button, Typography, Paper, IconButton, CircularProgress } from '@mui/material';
import KeyIcon from '@mui/icons-material/Key';
import RefreshIcon from '@mui/icons-material/Refresh';
import { alpha } from '@mui/material/styles';

const KeywordsTask = ({
  metadata,
  onGenerate,
  onRegenerateKeyword,
  isGeneratingKeywords,
  isGeneratingKeyword,
  generateButtonStyle
}) => {
  return (
    <Box sx={{ mt: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
        <Button
          variant="contained"
          color="primary"
          startIcon={isGeneratingKeywords ? <CircularProgress size={20} color="inherit" /> : <KeyIcon />}
          onClick={onGenerate}
          disabled={isGeneratingKeywords}
          sx={generateButtonStyle}
        >
          {isGeneratingKeywords ? 'GENERAZIONE IN CORSO...' : 'GENERA KEYWORDS'}
        </Button>
      </Box>

      {metadata?.keywords?.length > 0 && (
        <Box sx={{ mt: 3 }}>
          {metadata.keywords.map((keyword, index) => (
            <Paper key={index} sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <Typography variant="subtitle2" color="primary" sx={{ flex: 1 }}>
                  Keyword {index + 1}
                </Typography>
                <IconButton 
                  size="small"
                  onClick={() => onRegenerateKeyword(index)}
                  disabled={isGeneratingKeyword}
                >
                  {isGeneratingKeyword ? 
                    <CircularProgress size={20} /> : 
                    <RefreshIcon />
                  }
                </IconButton>
              </Box>
              <Typography variant="body2">
                {keyword}
              </Typography>
            </Paper>
          ))}
        </Box>
      )}
    </Box>
  );
};

export default KeywordsTask;
