import React from 'react';
import {
  Box,
  Container,
  Typography,
  IconButton,
  Paper,
  Button
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { useNavigate, useLocation } from 'react-router-dom';

const CoverSelectionDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { scene } = location.state || {};

  return (
    <Box sx={{ backgroundColor: '#F8F9FF', minHeight: '100vh' }}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        {/* Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <IconButton 
            onClick={() => navigate(-1)}
            sx={{ 
              mr: 2,
              color: 'text.secondary',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.04)'
              }
            }}
          >
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h5" component="h1">
            {scene?.title || 'Selezione Copertina'}
          </Typography>
        </Box>

        {/* AI Message */}
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center',
          backgroundColor: '#F8F9FF',
          borderRadius: '12px',
          p: 2,
          mb: 4,
          border: '1px solid',
          borderColor: 'primary.main'
        }}>
          <Typography color="primary">
            Ho identificato diversi stili di copertina che potrebbero adattarsi bene a questa scena. Sentiti libero di scegliere quello che preferisci e personalizzarlo come meglio credi.
          </Typography>
        </Box>

        {/* Cover Grid */}
        <Box sx={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(3, 1fr)',
          gap: 3
        }}>
          {[1, 2, 3].map((cover) => (
            <Paper
              key={cover}
              sx={{
                position: 'relative',
                aspectRatio: '2/3',
                overflow: 'hidden',
                '&:hover': {
                  boxShadow: '0px 8px 24px rgba(0, 0, 0, 0.12)',
                }
              }}
            >
              {/* Cover Image Placeholder */}
              <Box
                sx={{
                  width: '100%',
                  height: '100%',
                  backgroundColor: 'rgba(75, 107, 251, 0.05)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}
              >
                {/* Qui andr√† l'immagine della copertina */}
              </Box>

              {/* Actions Overlay */}
              <Box
                sx={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  p: 1,
                  display: 'flex',
                  gap: 1
                }}
              >
                <IconButton size="small" sx={{ color: 'white', backgroundColor: 'rgba(0,0,0,0.2)' }}>
                  <FavoriteBorderIcon />
                </IconButton>
                <IconButton size="small" sx={{ color: 'white', backgroundColor: 'rgba(0,0,0,0.2)' }}>
                  <MoreVertIcon />
                </IconButton>
              </Box>
            </Paper>
          ))}
        </Box>

        {/* Generate More Button */}
        <Box sx={{ mt: 4, textAlign: 'center' }}>
          <Button
            variant="contained"
            color="primary"
            size="large"
          >
            GENERA ALTRE 3 COPERTINE
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default CoverSelectionDashboard;
