import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Container,
  Paper,
} from '@mui/material';
import { useBooks } from '../../context/BookContext';
import UploadSection from './UploadSection';
import NotificationSnackbar from '../common/NotificationSnackbar';

/**
 * MainDashboard - Componente principale della dashboard
 * Gestisce il caricamento dei libri e la navigazione post-upload
 */
const MainDashboard = () => {
  const navigate = useNavigate();
  const { addBook, isUploading } = useBooks();
  // Stati per gestire messaggi di successo ed errore
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const generateButtonStyle = {
    minWidth: '200px',
    maxWidth: '300px',
    height: '48px',
    borderRadius: '8px',
    textTransform: 'uppercase',
    fontSize: '1rem',
    fontWeight: 500,
    boxShadow: '0 4px 12px rgba(150, 121, 105, 0.2)',
    '&:hover': {
      boxShadow: '0 6px 16px rgba(150, 121, 105, 0.3)',
      backgroundColor: 'primary.dark'
    }
  };

  /**
   * Gestisce il processo di upload del file
   * @param {File} file - Il file da caricare
   */
  const handleUpload = async (file) => {
    if (!file) return;

    // Validazione dell'estensione del file
    const validTypes = ['.pdf', '.doc', '.docx', '.txt'];
    const fileExtension = file.name.substring(file.name.lastIndexOf('.'));
    if (!validTypes.includes(fileExtension.toLowerCase())) {
      setError('Formato file non supportato. Usa PDF, DOC, DOCX o TXT.');
      return;
    }

    try {
      setError(null);
      const bookId = await addBook({ file });
      setSuccess('Libro caricato con successo');
      // Reindirizza alla pagina dei task dopo il caricamento
      navigate('/tasks', { 
        state: { 
          bookName: file.name.replace(/\.[^/.]+$/, "") 
        }
      });
    } catch (error) {
      setError(error.message || 'Errore durante il caricamento. Riprova.');
      console.error('Upload error:', error);
    }
  };

  // Reset degli stati delle notifiche
  const handleCloseSnackbar = () => {
    setSuccess(null);
    setError(null);
  };

  return (
    <Box sx={{ backgroundColor: 'background.default', minHeight: '100vh' }}>
      <Container maxWidth="lg">
        <Box sx={{ mt: 4, mb: 4, textAlign: 'center' }}>
          {/* Intestazione della dashboard */}
          <Typography variant="h2" component="h1" gutterBottom>
            Benvenuto!
          </Typography>
          <Typography variant="h5" color="text.secondary" gutterBottom>
            Carica il tuo libro per iniziare a ottimizzare la tua presenza online
          </Typography>

          {/* Sezione di upload */}
          <Paper sx={{ mt: 4, p: 6 }}>
            <UploadSection 
              onUpload={handleUpload}
              isUploading={isUploading}
              error={error}
            />
          </Paper>
        </Box>

        {/* Componente per le notifiche */}
        <NotificationSnackbar
          success={success}
          error={error}
          onClose={handleCloseSnackbar}
        />
      </Container>
    </Box>
  );
};

export default MainDashboard;
