import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  InputBase,
  Paper,
  IconButton,
  List,
  ListItem,
  ListItemText,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  ListSubheader,
  ListItemButton,
  ListItemIcon,
  Alert,
  Snackbar
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import StorageIcon from '@mui/icons-material/Storage';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import DashboardIcon from '@mui/icons-material/Dashboard';
import { useBooks } from '../../context/BookContext';

const Sidebar = () => {
  const { books, addBook, removeBook, updateBook, resetBookData } = useBooks();
  const navigate = useNavigate();
  const location = useLocation();
  const [searchTerm, setSearchTerm] = React.useState('');
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [selectedBook, setSelectedBook] = React.useState(null);
  const [renameDialogOpen, setRenameDialogOpen] = React.useState(false);
  const [newBookName, setNewBookName] = React.useState('');
  const [deleteDialogOpen, setDeleteDialogOpen] = React.useState(false);
  const [success, setSuccess] = React.useState(null);

  const handleMenuClick = (event, book) => {
    event.stopPropagation();
    setAnchorEl(event.currentTarget);
    setSelectedBook(book);
  };

  const handleMenuClose = (event) => {
    event?.stopPropagation?.();
    setAnchorEl(null);
  };

  const handleRenameClick = (event) => {
    handleMenuClose(event);
    setNewBookName(selectedBook.title);
    setRenameDialogOpen(true);
  };

  const handleRenameClose = () => {
    setRenameDialogOpen(false);
    setNewBookName('');
  };

  const handleRenameConfirm = () => {
    if (newBookName.trim() !== '') {
      updateBook(selectedBook.id, { ...selectedBook, title: newBookName.trim() });
      if (location.pathname === '/tasks') {
        navigate('/tasks', { 
          state: { bookName: newBookName.trim() },
          replace: true
        });
      }
      handleRenameClose();
      setSuccess('Libro rinominato con successo');
    }
  };

  const handleDeleteClick = (event) => {
    handleMenuClose(event);
    setDeleteDialogOpen(true);
  };

  const handleDeleteClose = () => {
    setDeleteDialogOpen(false);
  };

  const handleDeleteConfirm = () => {
    removeBook(selectedBook.id);
    handleDeleteClose();
    navigate('/');
    setSuccess('Libro eliminato con successo');
  };

  const handleResetClick = async (event) => {
    handleMenuClose(event);
    try {
      await resetBookData(selectedBook.id);
      setSuccess('Reset completato con successo');
    } catch (error) {
      console.error('Error resetting book:', error);
    }
  };

  const filteredBooks = books.filter(book => 
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const groupedBooks = filteredBooks.reduce((acc, book) => {
    if (!acc[book.date]) {
      acc[book.date] = [];
    }
    acc[book.date].push(book);
    return acc;
  }, {});

  const adminMenu = [
    {
      title: 'Dashboard',
      path: '/',
      icon: <DashboardIcon />,
      description: 'Torna alla dashboard principale'
    },
    {
      title: 'Storage',
      path: '/admin/storage',
      icon: <StorageIcon />,
      description: 'Gestisci lo storage dell\'applicazione'
    }
  ];

  const handleCloseSnackbar = () => {
    setSuccess(null);
  };

  return (
    <div className="bg-gray-900 text-white w-64 min-h-screen px-4 py-6">
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-center mb-6">
          <span className="bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
            App Name
          </span>
        </h1>
      </div>

      <nav className="space-y-2">
        {adminMenu.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className="flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors duration-200 hover:bg-gray-800 hover:text-blue-400"
          >
            <item.icon className="h-5 w-5" />
            <span className="font-medium">{item.title}</span>
          </Link>
        ))}
      </nav>

      <div className="absolute bottom-0 left-0 right-0 p-4">
        <div className="flex items-center space-x-3 px-4 py-3">
          <img
            src={userAvatar}
            alt="User avatar"
            className="h-8 w-8 rounded-full ring-2 ring-blue-400"
          />
          <div className="flex-1">
            <p className="text-sm font-medium">{userName}</p>
            <p className="text-xs text-gray-400">{userEmail}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
