import React from 'react';
import TaskCard from './TaskCard';
import { Box, Typography } from '@mui/material';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';

const KeywordsTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonStyle
}) => {
  const keywordsContent = (
    <>
      {currentBook.metadata?.keywords?.keywords?.map((keyword, index) => (
        <Box key={index} sx={{ mb: 2 }}>
          <Typography variant="body1">
            {keyword}
          </Typography>
        </Box>
      ))}
    </>
  );

  return (
    <TaskCard
      icon={LocalOfferIcon}
      title="Genera le parole chiave"
      description="Analizzerò il tuo libro e suggerirò quattro parole chiave pertinenti per migliorare la visibilità sugli store."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA KEYWORDS"
      generateButtonStyle={generateButtonStyle}
    >
      {keywordsContent}
    </TaskCard>
  );
};

export default KeywordsTaskCard;
