import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import DescriptionIcon from '@mui/icons-material/Description';
import CategoryIcon from '@mui/icons-material/Category';
import ImageIcon from '@mui/icons-material/Image';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import StoreIcon from '@mui/icons-material/Store';
import KeyIcon from '@mui/icons-material/Key';
import AutoStoriesIcon from '@mui/icons-material/AutoStories';
import { useBooks } from '../../contexts/BooksContext';

const Sidebar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { books } = useBooks();

  const bookIdFromUrl = location.pathname.split('/')[2];
  const bookId = bookIdFromUrl || (books.length > 0 ? books[0].id : null);

  const menuItems = [
    {
      title: 'Dashboard',
      path: '/',
      icon: <DashboardIcon />
    }
  ];

  const bookSections = [
    { 
      title: 'Categorie', 
      path: `/book/${bookId}/categories`,
      icon: <CategoryIcon />
    },
    { 
      title: 'Parole Chiave', 
      path: `/book/${bookId}/keywords`,
      icon: <KeyIcon />
    },
    { 
      title: 'Sinossi', 
      path: `/book/${bookId}/synopsis`,
      icon: <DescriptionIcon />
    },
    { 
      title: 'Quarta di Copertina', 
      path: `/book/${bookId}/backcover`,
      icon: <MenuBookIcon />
    },
    { 
      title: 'Prefazione', 
      path: `/book/${bookId}/preface`,
      icon: <AutoStoriesIcon />
    },
    { 
      title: 'Copertina', 
      path: `/book/${bookId}/cover`,
      icon: <ImageIcon />
    }
  ];

  return (
    <Box
      sx={{
        width: 280,
        height: '100vh',
        borderRight: '1px solid #E0E0E0',
        backgroundColor: '#FFFFFF',
        p: 3,
        position: 'fixed',
        left: 0,
        top: 0,
        overflowY: 'auto'
      }}
    >
      <Typography variant="h6" sx={{ mb: 3 }}>
        Book Editor
      </Typography>

      <List>
        {/* Dashboard Button */}
        {menuItems.map((item) => (
          <ListItem key={item.title} disablePadding>
            <ListItemButton
              onClick={() => navigate(item.path)}
              selected={location.pathname === item.path}
              sx={{
                borderRadius: '8px',
                mb: 1,
                '&.Mui-selected': {
                  backgroundColor: 'primary.lighter',
                  '&:hover': {
                    backgroundColor: 'primary.lighter'
                  }
                }
              }}
            >
              <ListItemIcon>
                {item.icon}
              </ListItemIcon>
              <ListItemText 
                primary={item.title}
                primaryTypographyProps={{
                  variant: 'body2',
                  fontWeight: location.pathname === item.path ? 600 : 400
                }}
              />
            </ListItemButton>
          </ListItem>
        ))}

        {/* Book Sections */}
        {bookSections.map((section) => (
          <ListItem key={section.title} disablePadding>
            <ListItemButton
              onClick={() => navigate(section.path)}
              selected={location.pathname === section.path}
              disabled={!bookId}
              sx={{
                borderRadius: '8px',
                mb: 0.5,
                '&.Mui-selected': {
                  backgroundColor: 'primary.lighter',
                  '&:hover': {
                    backgroundColor: 'primary.lighter'
                  }
                }
              }}
            >
              <ListItemIcon>
                {section.icon}
              </ListItemIcon>
              <ListItemText 
                primary={section.title}
                primaryTypographyProps={{
                  variant: 'body2',
                  fontWeight: location.pathname === section.path ? 600 : 400
                }}
              />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default Sidebar;
