import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Container,
  Paper,
  IconButton,
  Collapse,
  CircularProgress,
  Alert,
  Snackbar,
  MenuItem,
  ListItemIcon,
  ListItemText,
  Grid
} from '@mui/material';
import { alpha } from '@mui/material/styles';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import CategoryIcon from '@mui/icons-material/Category';
import KeyIcon from '@mui/icons-material/Key';
import RefreshIcon from '@mui/icons-material/Refresh';
import ImageIcon from '@mui/icons-material/Image';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import CoverIcon from '@mui/icons-material/Image';
import ArticleIcon from '@mui/icons-material/Article';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import StoreIcon from '@mui/icons-material/Store';
import FileDownloadIcon from '@mui/icons-material/FileDownload';
import { useBooks } from '../../context/BookContext';

const TaskDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { 
    books, 
    setBooks, 
    isGenerating, 
    generateCategories, 
    generateKeywords, 
    regenerateCategory, 
    regenerateKeyword, 
    generateScenes, 
    generateCoverImages, 
    resetBookData, 
    generateBackCover, 
    generatePreface, 
    generateStoreDescription 
  } = useBooks();
  const [currentBook, setCurrentBook] = useState(null);
  const [expandedCard, setExpandedCard] = useState(null);
  const [showCategories, setShowCategories] = useState(false);
  const [showKeywords, setShowKeywords] = useState(false);
  const [showCovers, setShowCovers] = useState(false);
  const [showBackCover, setShowBackCover] = useState(false);
  const [showPreface, setShowPreface] = useState(false);
  const [showStoreDescription, setShowStoreDescription] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [generatingStates, setGeneratingStates] = useState({
    main: false,
    secondary1: false,
    secondary2: false
  });
  const [isGeneratingAll, setIsGeneratingAll] = useState(false);
  const [isGeneratingKeywords, setIsGeneratingKeywords] = useState(false);
  const [isGeneratingCovers, setIsGeneratingCovers] = useState(false);
  const [isGeneratingBackCover, setIsGeneratingBackCover] = useState(false);
  const [isGeneratingPreface, setIsGeneratingPreface] = useState(false);
  const [isGeneratingStoreDescription, setIsGeneratingStoreDescription] = useState(false);
  const [keywordGeneratingStates, setKeywordGeneratingStates] = useState({
    main: false,
    secondary1: false,
    secondary2: false,
    secondary3: false
  });
  const [categories, setCategories] = useState(null);
  const [keywords, setKeywords] = useState(null);
  const [backCover, setBackCover] = useState(null);
  const [preface, setPreface] = useState(null);
  const [storeDescription, setStoreDescription] = useState(null);

  const isAnyTaskRunning = 
    isGeneratingAll || 
    isGeneratingKeywords || 
    isGeneratingCovers || 
    isGeneratingBackCover ||
    isGeneratingPreface ||
    isGeneratingStoreDescription ||
    Object.values(generatingStates).some(state => state) ||
    Object.values(keywordGeneratingStates).some(state => state);

  // Getters per accedere ai dati dal metadata del libro corrente
  const getCategories = () => currentBook?.metadata?.categories;
  const getKeywords = () => currentBook?.metadata?.keywords;
  const getCovers = () => currentBook?.metadata?.covers;
  const getBackCover = () => currentBook?.metadata?.backCover;
  const getPreface = () => currentBook?.metadata?.preface;
  const getStoreDescription = () => currentBook?.metadata?.storeDescription;

  useEffect(() => {
    const bookName = location.state?.bookName;
    if (bookName) {
      const book = books.find(b => b.title === bookName);
      if (book) {
        setCurrentBook(book);
        setExpandedCard(null);

        // Mostriamo le sezioni in base ai dati esistenti
        setShowCategories(!!book.metadata?.categories);
        setShowKeywords(!!book.metadata?.keywords?.length);
        setShowCovers(!!book.metadata?.covers?.scenes?.length);
        setShowBackCover(!!book.metadata?.backCover);
        setShowPreface(!!book.metadata?.preface);
        setShowStoreDescription(!!book.metadata?.storeDescription);
      }
    }
  }, [location.state, books]);

  const handleExpand = (cardId) => {
    if (
      (cardId === 'metadata' && isGeneratingAll) ||
      (cardId === 'keywords' && isGeneratingKeywords) ||
      (cardId === 'cover' && isGeneratingCovers) ||
      (cardId === 'backcover' && isGeneratingBackCover)
    ) {
      return;
    }

    setExpandedCard(expandedCard === cardId ? null : cardId);

    if (currentBook) {
      localStorage.setItem(`expandedCard_${currentBook.id}`, cardId || '');
    }
  };

  const handleGenerateCategories = async () => {
    try {
      setError(null);
      setIsGeneratingAll(true);
      setShowCategories(true);

      console.log('Generating categories for book:', currentBook.id);  // Log prima

      const result = await generateCategories(currentBook.id);

      console.log('Received categories:', result);  // Log dopo la chiamata

      setCategories(result);

      // Aggiorniamo anche il libro corrente
      const updatedBook = { ...currentBook };
      updatedBook.metadata = {
        ...updatedBook.metadata,
        categories: result
      };
      setCurrentBook(updatedBook);

      setSuccess('Categorie generate con successo');
      localStorage.setItem(`showCategories_${currentBook.id}`, 'true');
    } catch (error) {
      console.error('Error:', error);
      setError('Errore nella generazione delle categorie. Riprova.');
    } finally {
      setIsGeneratingAll(false);
    }
  };

  const handleGenerateKeywords = async () => {
    try {
      setError(null);
      setIsGeneratingKeywords(true);
      setShowKeywords(true);
      const result = await generateKeywords(currentBook.id);
      setKeywords(result);
      setSuccess('Keywords generate con successo');
      // Salva lo stato nel localStorage
      localStorage.setItem(`showKeywords_${currentBook.id}`, 'true');
    } catch (error) {
      console.error('Error:', error);
      setError('Errore nella generazione delle keywords. Riprova.');
    } finally {
      setIsGeneratingKeywords(false);
    }
  };

  const handleRegenerateCategory = async (type) => {
    try {
      setError(null);
      setGeneratingStates(prev => ({ ...prev, [type]: true }));

      if (!categories || !categories.mainCategory || !categories.secondaryCategories) {
        throw new Error('No categories available. Generate categories first.');
      }

      const currentCategories = {
        mainCategory: categories.mainCategory,
        secondaryCategories: categories.secondaryCategories || []
      };

      const result = await regenerateCategory(currentBook.id, type, currentCategories);

      if (type === 'main') {
        setCategories(prev => ({
          ...prev,
          mainCategory: result.category
        }));
      } else {
        const index = type === 'secondary1' ? 0 : 1;
        setCategories(prev => ({
          ...prev,
          secondaryCategories: prev.secondaryCategories.map((cat, idx) => 
            idx === index ? result.category : cat
          )
        }));
      }

      setSuccess(`Categoria ${type === 'main' ? 'principale' : 'secondaria'} rigenerata con successo`);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || `Errore nella rigenerazione della categoria ${type === 'main' ? 'principale' : 'secondaria'}. Riprova.`);
    } finally {
      setGeneratingStates(prev => ({ ...prev, [type]: false }));
    }
  };

  const handleRegenerateKeyword = async (index) => {
    try {
      setError(null);
      setKeywordGeneratingStates(prev => ({ ...prev, [`keyword${index}`]: true }));

      if (!keywords || !keywords.keywords) {
        throw new Error('No keywords available. Generate keywords first.');
      }

      const result = await regenerateKeyword(currentBook.id, index, keywords);

      setKeywords(prev => ({
        ...prev,
        keywords: prev.keywords.map((kw, idx) => 
          idx === index ? result.keyword : kw
        )
      }));

      setSuccess('Keyword rigenerata con successo');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Errore nella rigenerazione della keyword. Riprova.');
    } finally {
      setKeywordGeneratingStates(prev => ({ ...prev, [`keyword${index}`]: false }));
    }
  };

  const retryOperation = async (operation, maxRetries = 3) => {
    for (let i = 0; i < maxRetries; i++) {
      try {
        return await operation();
      } catch (error) {
        if (i === maxRetries - 1) throw error;
        await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
      }
    }
  };

  const handleGenerateCovers = async () => {
    try {
      setError(null);
      setSuccess(null);
      setIsGeneratingCovers(true);

      const scenesResult = await retryOperation(async () => {
        return await generateScenes(currentBook.id);
      });

      if (!scenesResult || !scenesResult.scenes) {
        throw new Error('Failed to generate scenes');
      }

      const coversResult = await retryOperation(async () => {
        return await generateCoverImages(currentBook.id, scenesResult.scenes);
      });

      // Preserviamo TUTTI i metadata esistenti
      const updatedBook = { ...currentBook };
      updatedBook.metadata = {
        ...currentBook.metadata,  // Preserva tutti i metadata esistenti incluse le keywords
        covers: {
          scenes: scenesResult.scenes.map((scene, index) => ({
            ...scene,
            imageUrl: coversResult.covers[index].imageUrl
          })),
          selectedScene: null
        }
      };

      // Aggiorniamo lo stato globale
      const bookIndex = books.findIndex(b => b.id === currentBook.id);
      if (bookIndex !== -1) {
        const newBooks = [...books];
        newBooks[bookIndex] = updatedBook;
        setBooks(newBooks);
        setCurrentBook(updatedBook);
      }

      setShowCovers(true);
      localStorage.setItem(`showCovers_${currentBook.id}`, 'true');
      setSuccess('Copertine generate con successo');

    } catch (error) {
      console.error('Error in handleGenerateCovers:', error);
      setError('Errore nella generazione delle copertine. Riprova.');
      setShowCovers(false);
      localStorage.removeItem(`showCovers_${currentBook.id}`);
    } finally {
      setIsGeneratingCovers(false);
    }
  };

  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(null);
  };

  const handleReset = async (bookId) => {
    try {
      setError(null);
      await resetBookData(bookId);
      setSuccess('Dati del libro resettati con successo');
    } catch (error) {
      console.error('Error:', error);
      setError('Errore nel reset dei dati del libro');
    }
  };

  const handleGenerateBackCover = async () => {
    try {
      setError(null);
      setIsGeneratingBackCover(true);
      setShowBackCover(true);

      console.log('Generating back cover for book:', currentBook.id);
      console.log('Current metadata before:', currentBook.metadata);  // Log per debug

      const result = await generateBackCover(currentBook.id);
      console.log('Received back cover:', result);

      // Aggiorniamo preservando tutti i metadata esistenti
      const updatedBook = {
        ...currentBook,
        metadata: {
          ...currentBook.metadata,  // Preserviamo tutti i metadata esistenti
          categories: currentBook.metadata.categories,  // Preserviamo esplicitamente le categorie
          keywords: currentBook.metadata.keywords,      // Preserviamo esplicitamente le keywords
          covers: currentBook.metadata.covers,          // Preserviamo esplicitamente le covers
          backCover: result.backCover                  // Aggiungiamo la quarta
        }
      };

      console.log('Updated metadata after:', updatedBook.metadata);  // Log per debug
      setCurrentBook(updatedBook);
      setBackCover(result.backCover);
      setSuccess('Quarta di copertina generata con successo');
      localStorage.setItem(`showBackCover_${currentBook.id}`, 'true');
    } catch (error) {
      console.error('Error:', error);
      setError('Errore nella generazione della quarta di copertina. Riprova.');
    } finally {
      setIsGeneratingBackCover(false);
    }
  };

  const handleMasterReset = async () => {
    if (window.confirm('Sei sicuro di voler resettare tutti i metadati? Questa azione non può essere annullata.')) {
      try {
        console.log('Starting master reset for book:', currentBook.id);
        setError(null);

        // Reset del libro nel database
        await resetBookData(currentBook.id);

        // Reset degli stati locali
        setCategories(null);
        setKeywords(null);
        setBackCover(null);
        setPreface(null);
        setStoreDescription(null);
        setShowCategories(false);
        setShowKeywords(false);
        setShowCovers(false);
        setShowBackCover(false);
        setShowPreface(false);
        setShowStoreDescription(false);
        setExpandedCard(null);
        setGeneratingStates({ main: false, secondary1: false, secondary2: false });
        setKeywordGeneratingStates({ main: false, secondary1: false, secondary2: false, secondary3: false });
        setIsGeneratingAll(false);
        setIsGeneratingKeywords(false);
        setIsGeneratingCovers(false);
        setIsGeneratingBackCover(false);
        setIsGeneratingPreface(false);
        setIsGeneratingStoreDescription(false);

        // Reset del localStorage
        localStorage.removeItem(`showCategories_${currentBook.id}`);
        localStorage.removeItem(`showKeywords_${currentBook.id}`);
        localStorage.removeItem(`showCovers_${currentBook.id}`);
        localStorage.removeItem(`showBackCover_${currentBook.id}`);
        localStorage.removeItem(`showPreface_${currentBook.id}`);
        localStorage.removeItem(`showStoreDescription_${currentBook.id}`);
        localStorage.removeItem(`expandedCard_${currentBook.id}`);

        console.log('Master reset completed successfully');
        setSuccess('Reset completo eseguito con successo');
      } catch (error) {
        console.error('Error in master reset:', error);
        setError('Errore durante il reset completo');
      }
    }
  };

  const handleGeneratePreface = async () => {
    try {
      setError(null);
      setIsGeneratingPreface(true);
      setShowPreface(true);

      console.log('Generating preface for book:', currentBook.id);
      console.log('Current metadata before:', currentBook.metadata);

      const result = await generatePreface(currentBook.id);
      console.log('Received preface:', result);

      setPreface(result.preface);
      setSuccess('Prefazione generata con successo');
      localStorage.setItem(`showPreface_${currentBook.id}`, 'true');
    } catch (error) {
      console.error('Error:', error);
      setError('Errore nella generazione della prefazione. Riprova.');
    } finally {
      setIsGeneratingPreface(false);
    }
  };

  const handleGenerateStoreDescription = async () => {
    try {
      setError(null);
      setIsGeneratingStoreDescription(true);
      setShowStoreDescription(true);

      console.log('Generating store description for book:', currentBook.id);
      console.log('Current metadata before:', currentBook.metadata);

      const result = await generateStoreDescription(currentBook.id);
      console.log('Received store description:', result);

      // Aggiorniamo preservando tutti i metadata esistenti
      const updatedBook = {
        ...currentBook,
        metadata: {
          ...currentBook.metadata,
          categories: currentBook.metadata.categories,
          keywords: currentBook.metadata.keywords,
          covers: currentBook.metadata.covers,
          backCover: currentBook.metadata.backCover,
          preface: currentBook.metadata.preface,
          storeDescription: result.storeDescription
        }
      };

      console.log('Updated metadata after:', updatedBook.metadata);
      setCurrentBook(updatedBook);
      setStoreDescription(result.storeDescription);
      setSuccess('Descrizione per gli store generata con successo');
      localStorage.setItem(`showStoreDescription_${currentBook.id}`, 'true');
    } catch (error) {
      console.error('Error:', error);
      setError('Errore nella generazione della descrizione. Riprova.');
    } finally {
      setIsGeneratingStoreDescription(false);
    }
  };

  if (!currentBook) return null;

  const generateButtonStyle = {
    minWidth: '200px',
    maxWidth: '300px',
    height: '48px',
    borderRadius: '8px',
    textTransform: 'uppercase',
    fontSize: '1rem',
    fontWeight: 500,
    boxShadow: '0 4px 12px rgba(75, 107, 251, 0.2)',
    '&:hover': {
      boxShadow: '0 6px 16px rgba(75, 107, 251, 0.3)',
      backgroundColor: 'primary.dark'
    }
  };

  return (
    <Box sx={{ backgroundColor: '#F8F9FA', minHeight: '100vh' }}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <IconButton 
            onClick={() => navigate('/')}
            sx={{ 
              mr: 2,
              color: 'text.secondary',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.04)'
              }
            }}
          >
            <ArrowBackIcon />
          </IconButton>
          <Box sx={{ flex: 1 }}>
            <Typography 
              variant="subtitle2" 
              color="text.secondary"
              sx={{ mb: 1 }}
            >
              {currentBook.date}
            </Typography>
            <Typography 
              variant="h5" 
              component="h1"
              sx={{ 
                fontWeight: 500,
                color: 'text.primary'
              }}
            >
              {currentBook.title}
            </Typography>
          </Box>
          <Button
            variant="outlined"
            color="error"
            startIcon={<RestartAltIcon />}
            onClick={() => handleMasterReset()}
            sx={{
              borderColor: 'error.light',
              '&:hover': {
                backgroundColor: 'error.lighter',
                borderColor: 'error.main'
              }
            }}
          >
            Reset Completo
          </Button>
        </Box>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box sx={{ mr: 3, backgroundColor: '#F8F9FF', borderRadius: '12px', p: 2 }}>
                  <CategoryIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Genera le categorie
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Analizzerò il tuo libro e suggerirò le categorie BISAC più appropriate per migliorare la visibilità sugli store.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('metadata')}
                    endIcon={expandedCard === 'metadata' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    disabled={isAnyTaskRunning}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'metadata'} timeout="auto" unmountOnExit>
                    <Box sx={{ mt: 3 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={isGeneratingAll ? <CircularProgress size={20} color="inherit" /> : <CategoryIcon />}
                          onClick={handleGenerateCategories}
                          disabled={isGeneratingAll}
                          sx={generateButtonStyle}
                        >
                          {isGeneratingAll ? 'GENERAZIONE IN CORSO...' : 'GENERA CATEGORIE'}
                        </Button>
                      </Box>

                      <Collapse in={showCategories} timeout={500}>
                        <Box sx={{ mt: 3 }}>
                          {/* Categoria Principale */}
                          <Paper sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <Typography variant="subtitle2" color="primary" sx={{ flex: 1 }}>
                                Categoria Principale
                              </Typography>
                              <IconButton 
                                size="small"
                                onClick={() => handleRegenerateCategory('main')}
                                disabled={generatingStates.main}
                              >
                                {generatingStates.main ? <CircularProgress size={20} /> : <RefreshIcon />}
                              </IconButton>
                            </Box>
                            <Typography variant="body2">
                              {getCategories()?.mainCategory || ''}
                            </Typography>
                          </Paper>

                          {/* Categorie Secondarie */}
                          {getCategories()?.secondaryCategories?.map((category, index) => (
                            <Paper key={index} sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                <Typography variant="subtitle2" color="primary" sx={{ flex: 1 }}>
                                  Categoria Secondaria {index + 1}
                                </Typography>
                                <IconButton 
                                  size="small"
                                  onClick={() => handleRegenerateCategory(`secondary${index + 1}`)}
                                  disabled={generatingStates[`secondary${index + 1}`]}
                                >
                                  {generatingStates[`secondary${index + 1}`] ? 
                                    <CircularProgress size={20} /> : 
                                    <RefreshIcon />
                                  }
                                </IconButton>
                              </Box>
                              <Typography variant="body2">
                                {category}
                              </Typography>
                            </Paper>
                          ))}
                        </Box>
                      </Collapse>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box sx={{ mr: 3, backgroundColor: '#F8F9FF', borderRadius: '12px', p: 2 }}>
                  <KeyIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Genera le parole chiave
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Identificherò le parole chiave più rilevanti per ottimizzare la visibilità del tuo libro nelle ricerche.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('keywords')}
                    endIcon={expandedCard === 'keywords' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    disabled={isAnyTaskRunning}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'keywords'} timeout="auto" unmountOnExit>
                    <Box sx={{ mt: 3 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={isGeneratingKeywords ? <CircularProgress size={20} color="inherit" /> : <KeyIcon />}
                          onClick={handleGenerateKeywords}
                          disabled={isGeneratingKeywords}
                          sx={generateButtonStyle}
                        >
                          {isGeneratingKeywords ? 'GENERAZIONE IN CORSO...' : 'GENERA KEYWORDS'}
                        </Button>
                      </Box>

                      <Collapse in={showKeywords} timeout={500}>
                        <Box sx={{ mt: 3 }}>
                          {getKeywords()?.length > 0 && (
                            <Box>
                              {getKeywords().map((keyword, index) => (
                                <Paper key={index} sx={{ p: 2, mb: 2, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                    <Typography variant="subtitle2" color="primary" sx={{ flex: 1 }}>
                                      Keyword {index + 1}
                                    </Typography>
                                    <IconButton 
                                      size="small"
                                      onClick={() => handleRegenerateKeyword(index)}
                                      disabled={keywordGeneratingStates[`keyword${index}`]}
                                    >
                                      {keywordGeneratingStates[`keyword${index}`] ? 
                                        <CircularProgress size={20} /> : 
                                        <RefreshIcon />
                                      }
                                    </IconButton>
                                  </Box>
                                  <Typography variant="body2">
                                    {keyword}
                                  </Typography>
                                </Paper>
                              ))}
                            </Box>
                          )}
                        </Box>
                      </Collapse>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper 
              sx={{ 
                p: 3,
                '&:hover': {
                  boxShadow: '0px 8px 24px rgba(0, 0, 0, 0.08)',
                }
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box 
                  sx={{ 
                    mr: 3,
                    backgroundColor: '#F8F9FF',
                    borderRadius: '12px',
                    p: 2
                  }}
                >
                  <CoverIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Crea un'immagine di copertina
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Sceglierò delle scene basandomi sul tuo manoscritto che ne rappresentino bene il contenuto e l'atmosfera. Dopodiché creerò una immagine di copertina per ognuna che potrai modificare, rigenerare completamente e scaricare in vari formati.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('cover')}
                    endIcon={expandedCard === 'cover' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    disabled={isAnyTaskRunning}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'cover'} timeout="auto" unmountOnExit>
                    <Box sx={{ 
                      mt: 3,
                      display: 'flex',
                      flexDirection: 'column',
                      gap: 2
                    }}>
                      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={isGeneratingCovers ? <CircularProgress size={20} color="inherit" /> : <ImageIcon />}
                          onClick={handleGenerateCovers}
                          disabled={isGeneratingCovers}
                          sx={generateButtonStyle}
                        >
                          {isGeneratingCovers ? 'GENERAZIONE IN CORSO...' : 'GENERA COPERTINE'}
                        </Button>
                      </Box>

                      <Collapse in={showCovers && !isGeneratingCovers} timeout="auto" unmountOnExit>
                        <Box sx={{ mt: 3 }}>
                          {/* AI Assistant Message */}
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center',
                            backgroundColor: '#F8F9FF',
                            borderRadius: '12px',
                            p: 2,
                            mb: 3
                          }}>
                            <ArticleIcon sx={{ color: 'primary.main', mr: 2 }} />
                            <Typography color="primary">
                              Ho identificato tre scene che potrebbero interessarti. Scegli quella che preferisci e personalizza le copertine come meglio credi.
                            </Typography>
                          </Box>

                          {/* Cover Cards */}
                          {[0, 1, 2].map((index) => (
                            <Paper
                              key={index}
                              sx={{
                                p: 3,
                                mb: 2,
                                backgroundColor: 'background.paper',
                                '&:hover': {
                                  boxShadow: '0px 8px 24px rgba(0, 0, 0, 0.08)',
                                }
                              }}
                            >
                              <Box sx={{ display: 'flex', gap: 3 }}>
                                {/* Cover Preview */}
                                <Box
                                  sx={{
                                    width: 180,
                                    height: 270,
                                    backgroundColor: alpha('#4B6BFB', 0.1),
                                    borderRadius: 2,
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    overflow: 'hidden'
                                  }}
                                >
                                  {currentBook?.metadata?.covers?.scenes?.[index]?.imageUrl ? (
                                    <img 
                                      src={currentBook.metadata.covers.scenes[index].imageUrl}
                                      alt={`Cover ${index + 1}`}
                                      style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                                    />
                                  ) : (
                                    <ImageIcon sx={{ fontSize: 48, color: 'primary.main', opacity: 0.5 }} />
                                  )}
                                </Box>

                                {/* Content */}
                                <Box sx={{ flex: 1 }}>
                                  <Typography variant="h6" gutterBottom>
                                    {currentBook?.metadata?.covers?.scenes?.[index]?.title || ''}
                                  </Typography>
                                  <Typography variant="body2" color="text.secondary" paragraph>
                                    {currentBook?.metadata?.covers?.scenes?.[index]?.description || ''}
                                  </Typography>

                                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                                    <Button
                                      variant="outlined"
                                      size="small"
                                      startIcon={<FileDownloadIcon />}
                                      onClick={() => navigate('/cover-selection', { 
                                        state: { 
                                          bookId: currentBook.id,
                                          scene: currentBook.metadata.covers.scenes[index],
                                          index: index
                                        }
                                      })}
                                    >
                                      VEDI COPERTINA
                                    </Button>
                                  </Box>
                                </Box>
                              </Box>
                            </Paper>
                          ))}
                        </Box>
                      </Collapse>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box sx={{ mr: 3, backgroundColor: '#F8F9FF', borderRadius: '12px', p: 2 }}>
                  <ArticleIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Crea la quarta di copertina
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Genererò una quarta di copertina accattivante che catturi l'attenzione del lettore e lo invogli ad acquistare il libro.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('backcover')}
                    endIcon={expandedCard === 'backcover' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    disabled={isAnyTaskRunning}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'backcover'} timeout="auto" unmountOnExit>
                    <Box sx={{ 
                      mt: 3,
                      display: 'flex',
                      flexDirection: 'column',
                      gap: 2
                    }}>
                      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={isGeneratingBackCover ? <CircularProgress size={20} color="inherit" /> : <ArticleIcon />}
                          onClick={handleGenerateBackCover}
                          disabled={isGeneratingBackCover}
                          sx={generateButtonStyle}
                        >
                          {isGeneratingBackCover ? 'GENERAZIONE IN CORSO...' : 'GENERA QUARTA'}
                        </Button>
                      </Box>

                      <Collapse in={showBackCover && !isGeneratingBackCover} timeout="auto" unmountOnExit>
                        <Box sx={{ mt: 3 }}>
                          {/* AI Assistant Message */}
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center',
                            backgroundColor: '#F8F9FF',
                            borderRadius: '12px',
                            p: 2,
                            mb: 3
                          }}>
                            <ArticleIcon sx={{ color: 'primary.main', mr: 2 }} />
                            <Typography color="primary">
                              La quarta di copertina è stata generata con successo.
                            </Typography>
                          </Box>

                          {/* Back Cover Text */}
                          <Paper sx={{ p: 3, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                            <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', lineHeight: 1.8 }}>
                              {backCover}
                            </Typography>
                          </Paper>

                          {/* Regenerate Button */}
                          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
                            <Button
                              variant="outlined"
                              color="primary"
                              startIcon={<RefreshIcon />}
                              onClick={handleGenerateBackCover}
                              disabled={isGeneratingBackCover}
                            >
                              RIGENERA
                            </Button>
                          </Box>
                        </Box>
                      </Collapse>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box sx={{ mr: 3, backgroundColor: '#F8F9FF', borderRadius: '12px', p: 2 }}>
                  <MenuBookIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Crea la prefazione
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Genererò una prefazione dettagliata ed esaustiva che introduca il lettore ai temi principali del libro, al contesto e al messaggio che vuoi trasmettere.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('preface')}
                    endIcon={expandedCard === 'preface' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    disabled={isAnyTaskRunning}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'preface'} timeout="auto" unmountOnExit>
                    <Box sx={{ 
                      mt: 3,
                      display: 'flex',
                      flexDirection: 'column',
                      gap: 2
                    }}>
                      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={isGeneratingPreface ? <CircularProgress size={20} color="inherit" /> : <MenuBookIcon />}
                          onClick={handleGeneratePreface}
                          disabled={isGeneratingPreface}
                          sx={generateButtonStyle}
                        >
                          {isGeneratingPreface ? 'GENERAZIONE IN CORSO...' : 'GENERA PREFAZIONE'}
                        </Button>
                      </Box>

                      <Collapse in={showPreface && !isGeneratingPreface} timeout="auto" unmountOnExit>
                        <Box sx={{ mt: 3 }}>
                          {/* AI Assistant Message */}
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center',
                            backgroundColor: '#F8F9FF',
                            borderRadius: '12px',
                            p: 2,
                            mb: 3
                          }}>
                            <MenuBookIcon sx={{ color: 'primary.main', mr: 2 }} />
                            <Typography color="primary">
                              La prefazione è stata generata con successo.
                            </Typography>
                          </Box>

                          {/* Preface Text */}
                          <Paper sx={{ p: 3, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                            <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', lineHeight: 1.8 }}>
                              {preface}
                            </Typography>
                          </Paper>

                          {/* Regenerate Button */}
                          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
                            <Button
                              variant="outlined"
                              color="primary"
                              startIcon={<RefreshIcon />}
                              onClick={handleGeneratePreface}
                              disabled={isGeneratingPreface}
                            >
                              RIGENERA
                            </Button>
                          </Box>
                        </Box>
                      </Collapse>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                <Box sx={{ mr: 3, backgroundColor: '#F8F9FF', borderRadius: '12px', p: 2 }}>
                  <StoreIcon sx={{ fontSize: 48, color: 'primary.main' }} />
                </Box>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    Crea la descrizione per gli store
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Genererò una descrizione accattivante e professionale per gli store online, ottimizzata per catturare l'attenzione dei potenziali lettori.
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => handleExpand('store')}
                    endIcon={expandedCard === 'store' ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    disabled={isAnyTaskRunning}
                  >
                    ESPANDI
                  </Button>

                  <Collapse in={expandedCard === 'store'} timeout="auto" unmountOnExit>
                    <Box sx={{ mt: 3 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={isGeneratingStoreDescription ? <CircularProgress size={20} color="inherit" /> : <StoreIcon />}
                          onClick={handleGenerateStoreDescription}
                          disabled={isGeneratingStoreDescription}
                          sx={generateButtonStyle}
                        >
                          {isGeneratingStoreDescription ? 'GENERAZIONE IN CORSO...' : 'GENERA DESCRIZIONE'}
                        </Button>
                      </Box>

                      <Collapse in={showStoreDescription && !isGeneratingStoreDescription} timeout="auto" unmountOnExit>
                        <Box sx={{ mt: 3 }}>
                          {/* AI Assistant Message */}
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center',
                            backgroundColor: '#F8F9FF',
                            borderRadius: '12px',
                            p: 2,
                            mb: 3
                          }}>
                            <StoreIcon sx={{ color: 'primary.main', mr: 2 }} />
                            <Typography color="primary">
                              La descrizione per gli store è stata generata con successo.
                            </Typography>
                          </Box>

                          {/* Store Description Text */}
                          <Paper sx={{ p: 3, backgroundColor: alpha('#4B6BFB', 0.02) }}>
                            <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', lineHeight: 1.8 }}>
                              {storeDescription}
                            </Typography>
                          </Paper>

                          {/* Regenerate Button */}
                          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
                            <Button
                              variant="outlined"
                              color="primary"
                              startIcon={<RefreshIcon />}
                              onClick={handleGenerateStoreDescription}
                              disabled={isGeneratingStoreDescription}
                            >
                              RIGENERA
                            </Button>
                          </Box>
                        </Box>
                      </Collapse>
                    </Box>
                  </Collapse>
                </Box>
              </Box>
            </Paper>
          </Grid>
        </Grid>

        <Box sx={{ mt: 4, textAlign: 'center' }}>
          <Typography variant="body2" color="text.secondary">
            © 2024 Marco Luigi Palma - All rights reserved.
          </Typography>
          <Box sx={{ mt: 1 }}>
            <Button color="primary" size="small">View Privacy Policy</Button>
            <Button color="primary" size="small">Terms & Conditions</Button>
          </Box>
        </Box>

        {/* Snackbars per feedback */}
        <Snackbar 
          open={!!error} 
          autoHideDuration={6000} 
          onClose={handleCloseSnackbar}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
            {error}
          </Alert>
        </Snackbar>

        <Snackbar 
          open={!!success} 
          autoHideDuration={3000} 
          onClose={handleCloseSnackbar}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
            {success}
          </Alert>
        </Snackbar>
      </Container>
    </Box>
  );
};

export default TaskDashboard;
