import React from 'react';
import TaskCard from './TaskCard';
import { Typography } from '@mui/material';
import ArticleIcon from '@mui/icons-material/Article';

const BackCoverTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  onRegenerate,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={ArticleIcon}
      title="Crea la quarta di copertina"
      description="GenererÃ² una quarta di copertina accattivante che catturi l'attenzione del lettore e lo invogli ad acquistare il libro."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA QUARTA"
      onRegenerate={onRegenerate}
      generateButtonStyle={generateButtonStyle}
    >
      <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', lineHeight: 1.8 }}>
        {currentBook.metadata?.backCover?.backCover || ''}
      </Typography>
    </TaskCard>
  );
};

export default BackCoverTaskCard;
