import React from 'react';
import TaskCard from './TaskCard';
import MenuBookIcon from '@mui/icons-material/MenuBook';

const PrefaceTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={MenuBookIcon}
      title="Genera la prefazione"
      description="Analizzerò il tuo libro e creerò una prefazione che introduca efficacemente i temi principali."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA PREFAZIONE"
      generateButtonStyle={generateButtonStyle}
    >
      {currentBook.metadata?.preface?.preface || ''}
    </TaskCard>
  );
};

export default PrefaceTaskCard;
