import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Layout from './components/layout/Layout';
import Dashboard from './components/dashboard/Dashboard';
import SynopsisSection from './components/sections/SynopsisSection';
import { ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Box } from '@mui/material';
import theme from './utils/theme';
import { BookProvider } from './context/BookContext';
import Sidebar from './components/layout/Sidebar';
import MainDashboard from './components/dashboard/MainDashboard';
import TaskDashboard from './components/dashboard/TaskDashboard';
import CoverSelectionDashboard from './components/cover/CoverSelectionDashboard';
import StorageManager from './components/admin/StorageManager';

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <BookProvider>
        <BrowserRouter>
          <Layout>
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/synopsis" element={<SynopsisSection />} />
              <Route path="/back-cover" element={<div>Back Cover Section</div>} />
              <Route path="/categories" element={<div>Categories Section</div>} />
              <Route path="/keywords" element={<div>Keywords Section</div>} />
              <Route path="/preface" element={<div>Preface Section</div>} />
              <Route path="/cover" element={<div>Cover Section</div>} />
            </Routes>
          </Layout>
        </BrowserRouter>
      </BookProvider>
    </ThemeProvider>
  );
}

export default App;
