import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  InputBase,
  Paper,
  IconButton,
  List,
  ListItem,
  ListItemText,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  ListSubheader,
  ListItemButton,
  ListItemIcon,
  Alert,
  Snackbar
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import StorageIcon from '@mui/icons-material/Storage';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import DashboardIcon from '@mui/icons-material/Dashboard';
import { useBooks } from '../../context/BookContext';

const Sidebar = () => {
  const { books, addBook, removeBook, updateBook, resetBookData } = useBooks();
  const navigate = useNavigate();
  const location = useLocation();
  const [searchTerm, setSearchTerm] = React.useState('');
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [selectedBook, setSelectedBook] = React.useState(null);
  const [renameDialogOpen, setRenameDialogOpen] = React.useState(false);
  const [newBookName, setNewBookName] = React.useState('');
  const [deleteDialogOpen, setDeleteDialogOpen] = React.useState(false);
  const [success, setSuccess] = React.useState(null);

  const handleMenuClick = (event, book) => {
    event.stopPropagation();
    setAnchorEl(event.currentTarget);
    setSelectedBook(book);
  };

  const handleMenuClose = (event) => {
    event?.stopPropagation?.();
    setAnchorEl(null);
  };

  const handleRenameClick = (event) => {
    handleMenuClose(event);
    setNewBookName(selectedBook.title);
    setRenameDialogOpen(true);
  };

  const handleRenameClose = () => {
    setRenameDialogOpen(false);
    setNewBookName('');
  };

  const handleRenameConfirm = () => {
    if (newBookName.trim() !== '') {
      updateBook(selectedBook.id, { ...selectedBook, title: newBookName.trim() });
      if (location.pathname === '/tasks') {
        navigate('/tasks', { 
          state: { bookName: newBookName.trim() },
          replace: true
        });
      }
      handleRenameClose();
      setSuccess('Libro rinominato con successo');
    }
  };

  const handleDeleteClick = (event) => {
    handleMenuClose(event);
    setDeleteDialogOpen(true);
  };

  const handleDeleteClose = () => {
    setDeleteDialogOpen(false);
  };

  const handleDeleteConfirm = () => {
    removeBook(selectedBook.id);
    handleDeleteClose();
    navigate('/');
    setSuccess('Libro eliminato con successo');
  };

  const handleResetClick = async (event) => {
    handleMenuClose(event);
    try {
      await resetBookData(selectedBook.id);
      setSuccess('Reset completato con successo');
    } catch (error) {
      console.error('Error resetting book:', error);
    }
  };

  const filteredBooks = books.filter(book => 
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const groupedBooks = filteredBooks.reduce((acc, book) => {
    if (!acc[book.date]) {
      acc[book.date] = [];
    }
    acc[book.date].push(book);
    return acc;
  }, {});

  const adminMenu = [
    {
      title: 'Dashboard',
      path: '/',
      icon: <DashboardIcon />,
      description: 'Torna alla dashboard principale'
    },
    {
      title: 'Storage',
      path: '/admin/storage',
      icon: <StorageIcon />,
      description: 'Gestisci lo storage dell\'applicazione'
    }
  ];

  const bookSections = [
    { title: 'Sinossi', path: 'synopsis' },
    { title: 'Categorie', path: 'categories' },
    { title: 'Copertina', path: 'cover' },
    { title: 'Quarta di copertina', path: 'backcover' },
    { title: 'Prefazione', path: 'preface' },
    { title: 'Descrizione Store', path: 'store' },
    { title: 'Parole chiave', path: 'keywords' }
  ];

  const handleCloseSnackbar = () => {
    setSuccess(null);
  };

  const renderBookSections = (book) => (
    <List>
      <ListSubheader 
        component="div" 
        sx={{ 
          backgroundColor: 'transparent',
          color: 'text.secondary',
          lineHeight: '24px',
          fontSize: '0.75rem',
          fontWeight: 700,
          textTransform: 'uppercase',
          letterSpacing: '0.5px'
        }}
      >
        Metadati libro
      </ListSubheader>
      {bookSections.map((section) => (
        <ListItem key={section.path} disablePadding>
          <ListItemButton
            onClick={() => navigate(`/book/${book.id}/${section.path}`)}
            selected={location.pathname === `/book/${book.id}/${section.path}`}
            sx={{
              borderRadius: '8px',
              mb: 0.5,
              '&.Mui-selected': {
                backgroundColor: 'primary.lighter',
                '&:hover': {
                  backgroundColor: 'primary.lighter'
                }
              }
            }}
          >
            <ListItemText 
              primary={section.title}
              primaryTypographyProps={{
                variant: 'body2',
                fontWeight: location.pathname === `/book/${book.id}/${section.path}` ? 600 : 400
              }}
            />
          </ListItemButton>
        </ListItem>
      ))}
    </List>
  );

  return (
    <Box
      sx={{
        width: 280,
        height: '100vh',
        borderRight: '1px solid #E0E0E0',
        backgroundColor: '#FFFFFF',
        p: 3,
        position: 'fixed',
        left: 0,
        top: 0,
        overflowY: 'auto'
      }}
    >
      <Typography variant="h6" sx={{ mb: 3 }}>
        Your books
      </Typography>

      <Paper
        sx={{
          p: '2px 4px',
          display: 'flex',
          alignItems: 'center',
          mb: 3,
          boxShadow: 'none',
          border: '1px solid #E0E0E0',
        }}
      >
        <InputBase
          sx={{ ml: 1, flex: 1 }}
          placeholder="search something..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <IconButton type="button" sx={{ p: '10px' }} aria-label="search">
          <SearchIcon />
        </IconButton>
      </Paper>

      {Object.entries(groupedBooks).map(([date, dateBooks]) => (
        <Box key={date}>
          <Typography 
            variant="subtitle2" 
            color="text.secondary"
            sx={{ mb: 2, mt: 3 }}
          >
            {date}
          </Typography>

          <List sx={{ p: 0 }}>
            {dateBooks.map((book) => (
              <ListItem
                key={book.id}
                sx={{
                  backgroundColor: location.pathname.includes(`/book/${book.id}/`) ? 
                                 '#F8F9FF' : 'transparent',
                  borderRadius: '8px',
                  mb: 1,
                  cursor: 'pointer',
                  '&:hover': {
                    backgroundColor: '#F8F9FF',
                  }
                }}
                onClick={() => navigate(`/book/${book.id}/synopsis`)}
                secondaryAction={
                  <IconButton 
                    edge="end"
                    onClick={(e) => handleMenuClick(e, book)}
                  >
                    <MoreVertIcon />
                  </IconButton>
                }
              >
                <InsertDriveFileIcon 
                  sx={{ 
                    mr: 2, 
                    color: 'primary.main',
                    fontSize: 20
                  }} 
                />
                <ListItemText 
                  primary={book.title}
                  primaryTypographyProps={{
                    variant: 'body2',
                    fontWeight: 500
                  }}
                />
              </ListItem>
            ))}
          </List>

          {dateBooks.map((book) => 
            location.pathname.includes(`/book/${book.id}/`) && renderBookSections(book)
          )}
        </Box>
      ))}

      <Box sx={{ mt: 4, borderTop: '1px solid #E0E0E0', pt: 2 }}>
        <List
          subheader={
            <ListSubheader component="div" sx={{ backgroundColor: 'transparent' }}>
              Admin
            </ListSubheader>
          }
        >
          {adminMenu.map((item) => (
            <ListItem key={item.title} disablePadding>
              <ListItemButton
                onClick={() => navigate(item.path)}
                selected={location.pathname === item.path}
                sx={{
                  borderRadius: '8px',
                  mb: 0.5,
                  '&.Mui-selected': {
                    backgroundColor: 'primary.lighter',
                    '&:hover': {
                      backgroundColor: 'primary.lighter'
                    }
                  }
                }}
              >
                <ListItemIcon sx={{ minWidth: 40 }}>
                  {item.icon}
                </ListItemIcon>
                <ListItemText 
                  primary={item.title} 
                  secondary={item.description}
                  primaryTypographyProps={{
                    variant: 'body2',
                    fontWeight: location.pathname === item.path ? 600 : 400
                  }}
                  secondaryTypographyProps={{
                    variant: 'caption'
                  }}
                />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Box>

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
        onClick={handleMenuClose}
      >
        <MenuItem onClick={handleRenameClick}>
          <EditIcon sx={{ mr: 1, fontSize: 20 }} />
          Rename
        </MenuItem>
        <MenuItem onClick={handleResetClick}>
          <RestartAltIcon sx={{ mr: 1, fontSize: 20 }} />
          Reset Data
        </MenuItem>
        <MenuItem onClick={handleDeleteClick} sx={{ color: 'error.main' }}>
          <DeleteIcon sx={{ mr: 1, fontSize: 20 }} />
          Delete
        </MenuItem>
      </Menu>

      <Dialog open={renameDialogOpen} onClose={handleRenameClose}>
        <DialogTitle>Rename Book</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Book Name"
            type="text"
            fullWidth
            value={newBookName}
            onChange={(e) => setNewBookName(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleRenameClose}>Cancel</Button>
          <Button onClick={handleRenameConfirm} color="primary">
            Rename
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={deleteDialogOpen} onClose={handleDeleteClose}>
        <DialogTitle>Delete Book</DialogTitle>
        <DialogContent>
          Are you sure you want to delete "{selectedBook?.title}"?
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDeleteClose}>Cancel</Button>
          <Button onClick={handleDeleteConfirm} color="error">
            Delete
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar 
        open={!!success} 
        autoHideDuration={3000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
          {success}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Sidebar;
