import { useState, useEffect, createContext, useContext } from 'react';
import { bookService } from '../services/api';

const BookContext = createContext();

export const BookProvider = ({ children }) => {
  const [books, setBooks] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);

  useEffect(() => {
    loadBooks();
  }, []);

  const loadBooks = async () => {
    try {
      const data = await bookService.getBooks();
      setBooks(data);
    } catch (error) {
      console.error('Error loading books:', error);
    }
  };

  const addBook = async (bookData) => {
    try {
      setIsUploading(true);
      const response = await bookService.uploadBook(bookData.file);
      setBooks(prevBooks => [...prevBooks, response.book]);
      return response.book.id;
    } catch (error) {
      console.error('Error adding book:', error);
      throw error;
    } finally {
      setIsUploading(false);
    }
  };

  const removeBook = async (bookId) => {
    try {
      await bookService.deleteBook(bookId);
      setBooks(prevBooks => prevBooks.filter(book => book.id !== bookId));
    } catch (error) {
      console.error('Error removing book:', error);
      throw error;
    }
  };

  const updateBook = async (bookId, updatedBook) => {
    try {
      const response = await bookService.updateBook(bookId, updatedBook);
      setBooks(prevBooks => 
        prevBooks.map(book => 
          book.id === bookId ? response.book : book
        )
      );
    } catch (error) {
      console.error('Error updating book:', error);
      throw error;
    }
  };

  const generateCategories = async (bookId) => {
    try {
      setIsGenerating(true);
      const response = await bookService.generateCategories(bookId);
      return response;
    } catch (error) {
      console.error('Error generating categories:', error);
      throw error;
    } finally {
      setIsGenerating(false);
    }
  };

  const generateKeywords = async (bookId) => {
    try {
      setIsGenerating(true);
      const response = await bookService.generateKeywords(bookId);
      return response;
    } catch (error) {
      console.error('Error generating keywords:', error);
      throw error;
    } finally {
      setIsGenerating(false);
    }
  };

  const regenerateCategory = async (bookId, type, currentCategories) => {
    try {
      setIsGenerating(true);
      const response = await bookService.regenerateCategory(bookId, type, currentCategories);
      return response;
    } catch (error) {
      console.error('Error regenerating category:', error);
      throw error;
    } finally {
      setIsGenerating(false);
    }
  };

  const regenerateKeyword = async (bookId, index, currentKeywords) => {
    try {
      setIsGenerating(true);
      const response = await bookService.regenerateKeyword(bookId, index, currentKeywords);
      return response;
    } catch (error) {
      console.error('Error regenerating keyword:', error);
      throw error;
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <BookContext.Provider value={{ 
      books, 
      addBook, 
      removeBook, 
      updateBook,
      isUploading,
      isGenerating,
      generateCategories,
      generateKeywords,
      regenerateCategory,
      regenerateKeyword
    }}>
      {children}
    </BookContext.Provider>
  );
};

export const useBooks = () => useContext(BookContext);
