import React from 'react';
import TaskCard from './TaskCard';
import DescriptionIcon from '@mui/icons-material/Description';

const BackCoverTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={DescriptionIcon}
      title="Genera la quarta di copertina"
      description="Analizzerò il tuo libro e creerò una quarta di copertina accattivante per attrarre i lettori."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA QUARTA"
      generateButtonStyle={generateButtonStyle}
    >
      {currentBook.metadata?.backCover?.backCover || ''}
    </TaskCard>
  );
};

export default BackCoverTaskCard;
