import React from 'react';
import TaskCard from './TaskCard';
import { Typography } from '@mui/material';
import StoreIcon from '@mui/icons-material/Store';

const StoreDescriptionTaskCard = ({
  currentBook,
  isExpanded,
  onExpand,
  isGenerating,
  onGenerate,
  showContent,
  generateButtonStyle
}) => {
  return (
    <TaskCard
      icon={StoreIcon}
      title="Crea la descrizione per gli store"
      description="GenererÃ² una descrizione accattivante e professionale per gli store online, ottimizzata per catturare l'attenzione dei potenziali lettori."
      isExpanded={isExpanded}
      onExpand={onExpand}
      isGenerating={isGenerating}
      onGenerate={onGenerate}
      showContent={showContent}
      generateButtonText="GENERA DESCRIZIONE"
      generateButtonStyle={generateButtonStyle}
    >
      <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', lineHeight: 1.8 }}>
        {currentBook.metadata?.storeDescription?.storeDescription || ''}
      </Typography>
    </TaskCard>
  );
};

export default StoreDescriptionTaskCard;
